// Anda perlu membuat file ini di backend, misal di backend/utils/numberToWords.js
// dan isinya bisa disalin dari frontend/src/utils/numberToWords.js
const { numberToWords } = require('./numberToWords');

function generateHeaderHTML(user) {
  if (!user) return '';
  // Gunakan variabel environment dari backend atau fallback ke localhost
  const baseUrl = process.env.APP_URL || 'http://localhost:5000';
  const logoUrl = user.logo ? `${baseUrl}/uploads/${user.logo}` : '';
  
  const logo = user.logo
    ? `<img src="${logoUrl}" alt="Logo" style="max-width:200px; max-height:80px; object-fit:contain; margin-left:auto;">`
    : '';

  return `
    <div style="display: flex; justify-content: space-between; align-items: flex-start; border-bottom: 4px solid black; padding-bottom: 10px; margin-bottom: 20px; font-family: sans-serif;">
      <div>
        <h1 style="margin: 0; font-size: 18px; font-weight: bold; text-transform: uppercase;">${user.nama || ''}</h1>
        <p style="margin: 5px 0; font-size: 12px;">${user.alamat || ''}</p>
        <p style="margin: 5px 0; font-size: 12px;">Telp: ${user.no_telp || ''} | Email: ${user.email || ''}</p>
      </div>
      ${logo ? `<div style="text-align:right;">${logo}</div>` : ''}
    </div>
  `;
}

function generateOfferTable(items = [], dynamicFields = {}) {
    const subTotal = items.reduce((sum, item) => sum + (Number(item.volume) || 0) * (Number(item.harga_satuan) || 0), 0);
    const ppn = Math.max(0, Number(dynamicFields.ppn_persen) || 0);
    const pph = Math.max(0, Number(dynamicFields.pph_persen) || 0);
    const ppnAmount = subTotal * (ppn / 100);
    const pphAmount = subTotal * (pph / 100);
    const grandTotal = subTotal + ppnAmount - pphAmount;

    const tableRows = items.map((item, index) => `
      <tr>
        <td style="border:2px solid #000;text-align:center;color:black">${index + 1}</td>
        <td style="border:2px solid #000;padding: 6px; text-align:left;color:black">${item.deskripsi || ''}</td>
        <td style="border:2px solid #000;padding: 6px; text-align:left;color:black">${item.tipe || ''}</td>
        <td style="border:2px solid #000;padding: 6px; text-align:left;color:black">${item.merek || ''}</td>
        <td style="border:2px solid #000;text-align:center;color:black">${item.volume || 0}</td>
        <td style="border:2px solid #000;text-align:right;padding: 6px;color:black">Rp ${Number(item.harga_satuan || 0).toLocaleString('id-ID')}</td>
        <td style="border:2px solid #000;text-align:right;padding: 6px;color:black">Rp ${(Number(item.volume || 0) * Number(item.harga_satuan || 0)).toLocaleString('id-ID')}</td>
      </tr>
    `).join('');

    return `
      <table style="width:100%;border-collapse: collapse;border:2px solid #000;font-size:12px">
        <thead>
          <tr style="background:#f2f2f2">
            <th style="border:2px solid #000; padding: 8px;">No</th>
            <th style="border:2px solid #000; padding: 8px;">Deskripsi</th>
            <th style="border:2px solid #000; padding: 8px;">Tipe</th>
            <th style="border:2px solid #000; padding: 8px;">Merek</th>
            <th style="border:2px solid #000; padding: 8px;">Volume</th>
            <th style="border:2px solid #000; padding: 8px;">Harga Satuan</th>
            <th style="border:2px solid #000; padding: 8px;">Total</th>
          </tr>
        </thead>
        <tbody>
          ${items.length ? tableRows : `<tr><td colspan="7" style="border:2px solid #000;text-align:center;padding:8px;"><i>Belum ada item</i></td></tr>`}
          <tr><td colspan="6" style="border:2px solid #000;text-align:right;padding: 6px;"><b>Sub Total</b></td><td style="border:2px solid #000;text-align:right;padding: 6px;">Rp ${subTotal.toLocaleString('id-ID')}</td></tr>
          <tr><td colspan="6" style="border:2px solid #000;text-align:right;padding: 6px;"><b>PPN ${ppn}%</b></td><td style="border:2px solid #000;text-align:right;padding: 6px;">Rp ${ppnAmount.toLocaleString('id-ID')}</td></tr>
          <tr><td colspan="6" style="border:2px solid #000;text-align:right;padding: 6px;"><b>PPh ${pph}%</b></td><td style="border:2px solid #000;text-align:right;padding: 6px;">Rp ${pphAmount.toLocaleString('id-ID')}</td></tr>
          <tr><td colspan="6" style="border:2px solid #000;text-align:right;padding: 6px;background:#f2f2f2"><b>Grand Total</b></td><td style="border:2px solid #000;text-align:right;padding: 6px;background:#f2f2f2">Rp ${grandTotal.toLocaleString('id-ID')}</td></tr>
        </tbody>
      </table>
    `;
}

function renderSuratTemplateOnServer(template, { surat, user, profil, kategori }) {
    if (!template) return '';
    
    let newContent = template;
    const { meta_data = {}, tanggal, no_surat } = surat;
    const { dynamicFields = {}, items = [] } = meta_data;
    const headerHTML = generateHeaderHTML(user);
    const tanggalFormatted = tanggal 
        ? new Date(tanggal).toLocaleDateString('id-ID', { day: 'numeric', month: 'long', year: 'numeric' })
        : '[Tanggal Surat]';

    newContent = newContent
        .replace(/{{kop_surat}}/g, headerHTML)
        .replace(/{{tanggal_surat}}/g, tanggalFormatted)
        .replace(/{{nama_penerima}}/g, profil?.nama || '[Nama Penerima]')
        .replace(/{{jabatan_penerima}}/g, profil?.jabatan || '[Jabatan]')
        .replace(/{{nomor_surat}}/g, no_surat || '[Nomor Surat]')
        .replace(/{{nama_penandatangan}}/g, dynamicFields.nama_penandatangan || user?.nama || '[Nama Penandatangan]')
        .replace(/{{jabatan_penandatangan}}/g, dynamicFields.jabatan_penandatangan || '[Jabatan Anda]')
        .replace(/{{nama_user}}/g, user?.nama || '[Nama Perusahaan]');

    if (kategori.kode === 'SP') {
        const tableHTML = generateOfferTable(items, dynamicFields);
        const ketPajak = `Penawaran sudah termasuk PPN ${dynamicFields.ppn_persen || 0}% dan PPh ${dynamicFields.pph_persen || 0}%`;
        const ketMasaBerlaku = `Masa berlaku penawaran ${dynamicFields.masa_berlaku || '14'} hari.`;

        newContent = newContent
            .replace('{{tabel_penawaran}}', tableHTML)
            .replace(/{{jenis_pekerjaan}}/g, dynamicFields.jenis_pekerjaan || '[Jenis Pekerjaan]')
            .replace('{{keterangan_pajak}}', ketPajak)
            .replace('{{ket_masa_berlaku}}', ketMasaBerlaku);
    } else if (kategori.kode === 'PP') {
        const jumlah = Number(dynamicFields.jumlah_tagihan) || 0;
        newContent = newContent
            .replace(/{{nama_pekerjaan}}/g, dynamicFields.nama_pekerjaan || '[Nama Pekerjaan]')
            .replace(/{{detail_pekerjaan}}/g, dynamicFields.detail_pekerjaan || '[Detail Pekerjaan]')
            .replace(/{{nomor_sp}}/g, dynamicFields.nomor_sp || '[Nomor SP]')
            .replace(/{{tanggal_sp}}/g, dynamicFields.tanggal_sp ? new Date(dynamicFields.tanggal_sp).toLocaleDateString('id-ID') : '[Tanggal SP]')
            .replace(/{{nomor_bast}}/g, dynamicFields.nomor_bast || '[Nomor BAST]')
            .replace(/{{tanggal_bast}}/g, dynamicFields.tanggal_bast ? new Date(dynamicFields.tanggal_bast).toLocaleDateString('id-ID') : '[Tanggal BAST]')
            .replace(/{{jumlah_tagihan}}/g, jumlah.toLocaleString('id-ID'))
            .replace(/{{terbilang_tagihan}}/g, jumlah > 0 ? numberToWords(jumlah) : 'Nol Rupiah')
            .replace(/{{nama_bank}}/g, dynamicFields.nama_bank || '[Nama Bank]')
            .replace(/{{nama_pemilik}}/g, dynamicFields.nama_pemilik || '[Nama Pemilik]')
            .replace(/{{nomor_rekening}}/g, dynamicFields.nomor_rekening || '[Nomor Rekening]');
    }

    return newContent;
}

module.exports = { renderSuratTemplateOnServer };
