import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getKwitansiById } from '../api';

// Helper function to format number to IDR currency
const formatRupiah = (number) => {
  if (isNaN(number)) return "Rp 0";
  return new Intl.NumberFormat('id-ID', {
    style: 'currency',
    currency: 'IDR',
    minimumFractionDigits: 0
  }).format(number);
};

const KwitansiDetailPages = () => {
    const [kwitansi, setKwitansi] = useState(null);
    const [loading, setLoading] = useState(true);
    const { id } = useParams();
    const navigate = useNavigate();

    // KOREKSI: Tentukan base URL untuk backend Anda di sini
    // Ganti 'http://localhost:5000' jika backend Anda berjalan di port yang berbeda
    const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';
    const logoUrl = `${API_URL}/uploads/logo_Maliki_Edulogi.png`;

    useEffect(() => {
        const fetchKwitansiDetail = async () => {
            try {
                const response = await getKwitansiById(id);
                setKwitansi(response.data.data);
            } catch (error) {
                console.error("Gagal mengambil detail kwitansi:", error);
            } finally {
                setLoading(false);
            }
        };
        fetchKwitansiDetail();
    }, [id]);

    const handlePrint = () => {
        window.print();
    };

    if (loading) return <div className="p-6 text-center">Memuat dokumen...</div>;
    if (!kwitansi) return <div className="p-6 text-center">Dokumen kwitansi tidak ditemukan.</div>;

    const printStyles = `
      @media print {
        body * {
          visibility: hidden;
        }
        .printable-area, .printable-area * {
          visibility: visible;
        }
        .printable-area {
          position: absolute;
          left: 0;
          top: 0;
          width: 100%;
        }
        .no-print {
          display: none;
        }
      }
    `;

    return (
        <div className="bg-gray-200 min-h-screen p-4 sm:p-8">
            <style>{printStyles}</style>
            
            <div className="action-buttons no-print max-w-4xl mx-auto mb-4 flex justify-between">
                <button onClick={() => navigate('/kwitansi')} className="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700">
                    Kembali
                </button>
                <button onClick={handlePrint} className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
                    Print Kwitansi
                </button>
            </div>

            <div className="printable-area bg-white max-w-4xl mx-auto p-8 sm:p-12 shadow-lg">
                {/* Header Section */}
                <header className="flex justify-between items-start border-b-2 border-black pb-4">
                    <div className="flex items-center">
                        {/* KOREKSI: URL gambar diubah untuk mengambil dari backend */}
                        <img src={logoUrl} alt="Logo Maliki" className="h-16 w-auto mr-4"/>
                        <div>
                            <h1 className="font-bold text-lg">CV. MALIKI EDULOGI NUSANTARA</h1>
                            <p className="text-xs">Jl. Ikan Kakap No.2A, Tunjungsekar, Kec. Lowokwaru, Kota Malang, Jawa Timur, 65158</p>
                            <p className="text-xs">Telp: 085736745916</p>
                            <p className="text-xs">Email: info@maliki.id</p>
                        </div>
                    </div>
                    <div className="text-right">
                        <h2 className="text-2xl font-bold text-blue-600">KWITANSI</h2>
                        <p className="text-sm">Kwitansi No. {kwitansi.no_kwitansi}</p>
                    </div>
                </header>

                {/* Details Section */}
                <main className="mt-8 text-sm">
                    <table className="w-full">
                        <tbody>
                            <tr>
                                <td className="font-semibold pr-4 w-1/4">Sudah terima dari</td>
                                <td className="w-3/4">: {kwitansi.profil?.nama || 'N/A'}</td>
                            </tr>
                            <tr className="h-4"><td></td><td></td></tr>
                            <tr>
                                <td className="font-semibold pr-4">Banyaknya Uang</td>
                                <td className="italic">: {kwitansi.terbilang ? `${kwitansi.terbilang} Rupiah` : 'Nol Rupiah'}</td>
                            </tr>
                            <tr className="h-4"><td></td><td></td></tr>
                            <tr>
                                <td className="font-semibold pr-4">Untuk pembayaran</td>
                                <td>: {kwitansi.untuk || 'N/A'}</td>
                            </tr>
                        </tbody>
                    </table>
                </main>
                
                {/* Footer and Signature Section */}
                <footer className="mt-12 flex justify-between items-end">
                    <div className="border-2 border-blue-600 p-3 self-end">
                        <p className="font-bold text-lg">Jumlah Rp {new Intl.NumberFormat('id-ID').format(kwitansi.jumlah || 0)},00</p>
                    </div>
                    <div className="text-center text-sm">
                        <p>
                            Malang, {new Date(kwitansi.tanggal).toLocaleDateString('id-ID', { day: 'numeric', month: 'long', year: 'numeric' })}
                        </p>
                        <p>CV. Maliki Edulogi Nusantara</p>
                        <div className="h-20"></div> {/* Spacer for signature */}
                        <p className="font-bold underline">Aprilia Nur Rahmawati</p>
                    </div>
                </footer>
            </div>
        </div>
    );
};

export default KwitansiDetailPages;
