// frontend/src/utils/renderInvoiceTemplate.js
import { numberToWords } from './numberToWords';

export function renderInvoiceTemplate(template, formData, dynamicFields, items, user, profil) {
  if (!template) return '';

  let newContent = template;

  // === 1. HEADER ===
  const baseUrl = process.env.REACT_APP_API_URL.replace('/api', ''); 
  const logoUrl = user?.logo ? `${baseUrl}/uploads/${user.logo}` : '';
  const logo = user?.logo
    ? `<img src="${logoUrl}" alt="Logo" style="max-width:310px; max-height:155px; object-fit:contain; margin-left:auto;">`
    : '';
  
  const headerHTML = `
    <div style="display: flex; justify-content: space-between; align-items: flex-start; border-bottom: 4px solid black; padding-bottom: 10px; margin-bottom: 20px;">
      <div>
        <h1 style="margin: 0; font-size: 18px; font-weight: bold; text-transform: uppercase;">${user?.nama || ''}</h1>
        <p style="margin: 5px 0; font-size: 12px;">${user?.alamat || ''}</p>
        <p style="margin: 5px 0; font-size: 12px;">Telp: ${user?.no_telp || ''} | Email: ${user?.email || ''}</p>
      </div>
      ${logo ? `<div style="text-align:right;">${logo}</div>` : ''}
    </div>
  `;

  // === 2. QR CODE ===
  const qrCodeHTML = formData.qrcode_base64
    ? `<img src="${formData.qrcode_base64}" alt="QR Code" style="width: 80px; height: 80px;">`
    : `<div style="width: 80px; height: 80px; border: 1px solid #ccc; font-size: 10px; display: flex; align-items: center; justify-content: center; color: #888;">QR akan muncul setelah disimpan</div>`;

  // === 3. KALKULASI & TABEL ITEM ===
  const subtotal = items.reduce((sum, item) => sum + ((Number(item.qty) || 0) * (Number(item.harga) || 0)), 0);
  const ppn_persen = Number(dynamicFields?.ppn_persen) || 0;
  const pph_persen = Number(dynamicFields?.pph_persen) || 0;
  const ongkir = Number(dynamicFields?.ongkir) || 0;
  const pembulatan = Number(dynamicFields?.pembulatan) || 0;

  const ppn_jumlah = subtotal * (ppn_persen / 100);
  const pph_jumlah = subtotal * (pph_persen / 100);
  const total_bayar = subtotal + ppn_jumlah - pph_jumlah + ongkir + pembulatan;
  const terbilang = numberToWords(total_bayar);

  const tableRows = items.map((item, index) => `
    <tr>
      <td style="border: 1px solid #333; text-align: center; padding: 6px;">${index + 1}</td>
      <td style="border: 1px solid #333; padding: 6px;">
        <strong>${item.produk || ''}</strong><br>
        <small>${item.deskripsi || ''}</small>
      </td>
      <td style="border: 1px solid #333; text-align: center; padding: 6px;">${item.qty || 0} ${item.satuan || ''}</td>
      <td style="border: 1px solid #333; text-align: right; padding: 6px;">Rp ${Number(item.harga).toLocaleString('id-ID')}</td>
      <td style="border: 1px solid #333; text-align: right; padding: 6px;">Rp ${((Number(item.qty) || 0) * (Number(item.harga) || 0)).toLocaleString('id-ID')}</td>
    </tr>
  `).join('');

  const tableHTML = `
    <table style="width: 100%; border-collapse: collapse; font-size: 12px; margin: 20px 0;">
      <thead>
        <tr style="background-color: #f2f2f2;">
          <th style="border: 1px solid #333; padding: 8px; width: 5%;">No</th>
          <th style="border: 1px solid #333; padding: 8px; text-align: left;">Produk/Jasa</th>
          <th style="border: 1px solid #333; padding: 8px; width: 15%;">Kuantitas</th>
          <th style="border: 1px solid #333; padding: 8px; text-align: right; width: 20%;">Harga Satuan</th>
          <th style="border: 1px solid #333; padding: 8px; text-align: right; width: 20%;">Jumlah</th>
        </tr>
      </thead>
      <tbody>
        ${items.length > 0 ? tableRows : `<tr><td colspan="5" style="text-align: center; padding: 10px;"><i>Belum ada item ditambahkan.</i></td></tr>`}
      </tbody>
      <tfoot>
        <tr><td colspan="4" style="text-align: right; padding: 6px;">Subtotal</td><td style="text-align: right; padding: 6px;">Rp ${subtotal.toLocaleString('id-ID')}</td></tr>
        <tr><td colspan="4" style="text-align: right; padding: 6px;">PPN (${ppn_persen}%)</td><td style="text-align: right; padding: 6px;">Rp ${ppn_jumlah.toLocaleString('id-ID')}</td></tr>
        <tr><td colspan="4" style="text-align: right; padding: 6px;">PPh (${pph_persen}%)</td><td style="text-align: right; padding: 6px;">- Rp ${pph_jumlah.toLocaleString('id-ID')}</td></tr>
        <tr><td colspan="4" style="text-align: right; padding: 6px;">Ongkos Kirim</td><td style="text-align: right; padding: 6px;">Rp ${ongkir.toLocaleString('id-ID')}</td></tr>
        <tr><td colspan="4" style="text-align: right; padding: 6px;">Pembulatan</td><td style="text-align: right; padding: 6px;">Rp ${pembulatan.toLocaleString('id-ID')}</td></tr>
        <tr style="font-weight: bold; background-color: #f2f2f2;"><td colspan="4" style="text-align: right; padding: 8px;">TOTAL</td><td style="text-align: right; padding: 8px;">Rp ${total_bayar.toLocaleString('id-ID')}</td></tr>
      </tfoot>
    </table>
  `;
  
  // === 4. DATA UMUM & GANTI PLACEHOLDER ===
  const selectedProfil = profil.find(p => p.id === Number(formData.profil_id));
  const tanggalFormatted = formData.tanggal
    ? new Date(formData.tanggal).toLocaleDateString('id-ID', { day: 'numeric', month: 'long', year: 'numeric' })
    : '[Tanggal Invoice]';

  newContent = newContent
    .replace('{{kop_surat}}', headerHTML)
    .replace('{{qrcode}}', qrCodeHTML)
    .replace('{{tabel_invoice}}', tableHTML)
    .replace(/{{nomor_invoice}}/g, formData.no_invoice || '.../.../.../.../...')
    .replace(/{{tanggal_invoice}}/g, tanggalFormatted)
    .replace(/{{nama_klien}}/g, selectedProfil?.nama || '[Nama Klien]')
    .replace(/{{alamat_klien}}/g, selectedProfil?.alamat || '[Alamat Klien]')
    .replace(/{{telepon_klien}}/g, selectedProfil?.telepon || '[Telepon Klien]')
    .replace(/{{terbilang}}/g, terbilang)
    .replace(/{{nama_penandatangan}}/g, dynamicFields?.nama_penandatangan || user?.nama || '[Nama Penandatangan]')
    .replace(/{{jabatan_penandatangan}}/g, dynamicFields?.jabatan_penandatangan || '[Jabatan]');

  return newContent;
}
