// frontend/src/pages/SuratDetailPages.js

import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
// ✅ Pustaka qrcode.react tidak lagi dibutuhkan di sini
import { getSuratById } from '../api'; 

// Styling CSS bisa dipindah ke file CSS terpisah jika diinginkan
const pageStyle = {
    background: 'white',
    width: '210mm',
    minHeight: '297mm',
    marginLeft: 'auto',
    marginRight: 'auto',
    padding: '20mm',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)',
};

const SuratDetailPages = () => {
    const [surat, setSurat] = useState(null);
    const [loading, setLoading] = useState(true);
    const { id } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        const fetchSuratDetail = async () => {
            try {
                const response = await getSuratById(id);
                setSurat(response.data.data);
            } catch (error) {
                console.error("Gagal mengambil detail surat:", error);
            } finally {
                setLoading(false);
            }
        };
        fetchSuratDetail();
    }, [id]);

    const handlePrint = () => {
        window.print();
    };

    if (loading) return <div className="p-6 text-center">Memuat dokumen...</div>;
    if (!surat) return <div className="p-6 text-center">Dokumen tidak ditemukan.</div>;

    // Ambil URL background dari data 'profil' yang berelasi dengan surat
    // Ini digunakan untuk background halaman, BUKAN header di dalam dokumen
    const backgroundImageUrl = surat.profil ? surat.profil.backgroundUrl : '';

    return (
        // Container utama untuk background halaman (tidak ikut di-print)
        <div className="document-container" style={{ backgroundImage: `url(${backgroundImageUrl})`, padding: '2rem' }}>
            
            {/* Tombol Aksi (tidak ikut di-print) */}
            <div className="action-buttons no-print p-6 max-w-4xl mx-auto flex justify-between">
                <button onClick={() => navigate('/surat')} className="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700">
                    Kembali
                </button>
                <button onClick={handlePrint} className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
                    Print Surat
                </button>
            </div>

            {/* ✅ KOREKSI: Ini adalah area kertas A4 yang akan di-print */}
            <div style={pageStyle} className="document-paper">
                {/* Seluruh isi surat, termasuk header, body, QR code, dan tanda tangan
                  sekarang dirender dari satu sumber: `surat.isi`.
                  Kita tidak lagi membuat ulang header atau signature di sini.
                */}
                <div dangerouslySetInnerHTML={{ __html: surat.isi }} />
            </div>

        </div>
    );
};

export default SuratDetailPages;
