import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { Navigate } from 'react-router-dom';
import { getProfil, getInvoice, createInvoice, updateInvoice, deleteInvoice } from '../api'; 
import InvoiceList from '../components/InvoiceList';
import InvoiceForm from '../components/InvoiceForm';
import { useTranslation } from '../hooks/useTranslation';

function Invoice() {
  const { isAuthenticated, loading } = useAuth();
  const { t } = useTranslation();
  const [invoice, setInvoice] = useState([]);
  const [profil, setProfil] = useState([]); 
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [entriesPerPage, setEntriesPerPage] = useState(10);
  const [editingInvoice, setEditingInvoice] = useState(null);

  if (loading) return <div>{t('Loading...')}</div>;
  if (!isAuthenticated()) return <Navigate to="/login" />;

  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const invoiceRes = await getInvoice();
        const invoiceData = invoiceRes.data?.data;
        if (Array.isArray(invoiceData)) {
          setInvoice(invoiceData);
        } else {
          console.warn('⚠️ Data invoice bukan array:', invoiceData);
          setInvoice([]);
        }

        const profilRes = await getProfil(); 
        const profilData = profilRes.data?.data;
        if (Array.isArray(profilData)) {
          setProfil(profilData);
        } else {
          console.warn('⚠️ Data profil bukan array:', profilData);
          setProfil([]);
        }

      } catch (err) {
        console.error('Gagal ambil data awal:', err);
        alert(t('Gagal memuat data. Cek koneksi atau database.'));
      }
    };
    fetchInitialData();
  }, []);

  const handleCreate = async (data) => {
    try {
      await createInvoice(data);
      const res = await getInvoice(); 
      setInvoice(res.data?.data || []);
      setEditingInvoice(null);
    } catch (err) {
      console.error('Gagal tambah invoice:', err);
      alert(t('Gagal menambahkan data invoice.'));
    }
  };

  const handleUpdate = async (id, data) => {
    try {
      await updateInvoice(id, data);
      const res = await getInvoice();
      setInvoice(res.data?.data || []);
      setEditingInvoice(null);
    } catch (err) {
      console.error('Gagal update invoice:', err);
      alert(t('Gagal memperbarui data invoice.'));
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm(t('Apakah Anda yakin ingin menghapus data ini?'))) {
      try {
        await deleteInvoice(id);
        const res = await getInvoice();
        setInvoice(res.data?.data || []);
      } catch (err) {
        console.error('Gagal hapus invoice:', err);
        alert(t('Gagal menghapus data invoice.'));
      }
    }
  };

  const handleEdit = (invoice) => {
    setEditingInvoice(invoice);
  };

  const handleCancelEdit = () => {
    setEditingInvoice(null);
  };

  const handleEntriesChange = (e) => {
    const value = Number(e.target.value);
    if (value > 0) {
      setEntriesPerPage(value);
      setCurrentPage(1);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">{t('Invoice Management')}</h1>
      <div className="flex justify-between items-center mb-6">
        <button
          onClick={() =>
            setEditingInvoice({
              id: null,
              no_invoice: '',
              tanggal: '',
              profil_id: null,
              kategori_kode: 'INV',
              items: [],
              ppn_persen: 0,
              pph_persen: 0,
              pembulatan: 0,
              ongkir: 0
            })
          }
          className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          {t('Create')}
        </button>
        <div className="relative">
          <input
            type="text"
            placeholder={t('Search')}
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <svg
            className="w-5 h-5 absolute left-3 top-2.5 text-gray-500"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
            ></path>
          </svg>
        </div>
      </div>

      <InvoiceList
        invoice={invoice}
        searchTerm={searchTerm}
        currentPage={currentPage}
        entriesPerPage={entriesPerPage}
        onPageChange={setCurrentPage}
        onEntriesChange={handleEntriesChange}
        onEdit={handleEdit}
        onDelete={handleDelete}
      />

      {editingInvoice && (
        <InvoiceForm
          formData={editingInvoice}
          setFormData={setEditingInvoice}
          onSubmit={(data) => {
            if (editingInvoice.id) {
              handleUpdate(editingInvoice.id, data);
            } else {
              handleCreate(data);
            }
          }}
          onCancel={handleCancelEdit}
          isEditing={!!editingInvoice.id}
          profil={profil}
        />
      )}
    </div>
  );
}

export default Invoice;
