// frontend/src/pages/SuratDetailPages.js

import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { QRCodeSVG } from 'qrcode.react'; 
import { getSuratById } from '../api'; 

const SuratDetailPages = () => {
    const [surat, setSurat] = useState(null);
    const [loading, setLoading] = useState(true);
    const { id } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        const fetchSuratDetail = async () => {
            try {
                // ✅ DIKOREKSI: Panggil fungsi getSuratById langsung dengan parameter 'id'
                const response = await getSuratById(id);
                setSurat(response.data.data); // Ambil data dari object response

            } catch (error) {
                console.error("Gagal mengambil detail surat:", error);
            } finally {
                setLoading(false);
            }
        };
        fetchSuratDetail();
    }, [id]);

    const handlePrint = () => {
        window.print();
    };

    if (loading) return <div className="p-6 text-center">Memuat dokumen...</div>;
    if (!surat) return <div className="p-6 text-center">Dokumen tidak ditemukan.</div>;

    const qrCodeUrl = window.location.href;
    const backgroundImageUrl = surat.profil ? surat.profil.backgroundUrl : '';

    return (
        <div className="document-container" style={{ backgroundImage: `url(${backgroundImageUrl})` }}>
            <div className="action-buttons no-print p-6">
                <button onClick={() => navigate('/surat')} className="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700">
                    Kembali
                </button>
                <button onClick={handlePrint} className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
                    Print Surat
                </button>
            </div>

            <div className="document-content p-12">
                <header className="text-center mb-8">
                    <h1 className="text-xl font-bold">{surat.profil?.nama || 'Nama Perusahaan'}</h1>
                    <p className="text-sm">{surat.profil?.alamat || 'Alamat Perusahaan'}</p>
                </header>
                
                <main>
                    <p><strong>Nomor:</strong> {surat.no_surat}</p>
                    <p><strong>Perihal:</strong> {surat.perihal}</p>
                    <hr className="my-4" />
                    <div dangerouslySetInnerHTML={{ __html: surat.isi }} />
                </main>

                <div className="signature-section mt-20 flex justify-between items-end">
                    <div className="qr-code-container">
                        <QRCodeSVG value={qrCodeUrl} size={80} />
                    </div>
                    <div className="signature-text text-center">
                        <p>Malang, {new Date(surat.tanggal).toLocaleDateString('id-ID', { day: 'numeric', month: 'long', year: 'numeric' })}</p>
                        <br /><br /><br />
                        <p className="font-bold underline">Nama Penandatangan</p>
                        <p>Jabatan</p>
                    </div>
                </div>
            </div>

            <footer className="document-footer text-center text-xs text-gray-500 p-6 border-t mt-8">
                <p>Dokumen ini diterbitkan oleh CV MALIKI EDULOGI NUSANTARA dan bersifat asli.</p>
            </footer>
        </div>
    );
};

export default SuratDetailPages;
