import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { Navigate } from 'react-router-dom';
import { getProfil, getKwitansi, createKwitansi, updateKwitansi, deleteKwitansi } from '../api'; 
import KwitansiList from '../components/KwitansiList'; 
import KwitansiForm from '../components/KwitansiForm';
import { useTranslation } from '../hooks/useTranslation';

function Kwitansi() {
  const { isAuthenticated, loading } = useAuth();
  const { t } = useTranslation();
  const [kwitansiList, setKwitansiList] = useState([]); 
  const [profilList, setProfilList] = useState([]);     
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [entriesPerPage, setEntriesPerPage] = useState(10);
  const [editingKwitansi, setEditingKwitansi] = useState(null);
  
  if (loading) return <div>{t('Loading...')}</div>;
  if (!isAuthenticated()) return <Navigate to="/login" />;

  const fetchKwitansi = async () => {
    try {
      const kwitansiRes = await getKwitansi();
      const kwitansiData = kwitansiRes.data?.data;
      if (Array.isArray(kwitansiData)) {
        setKwitansiList(kwitansiData);
      } else {
        console.warn('⚠️ Data kwitansi bukan array:', kwitansiData);
        setKwitansiList([]);
      }
    } catch (err) {
      console.error('Gagal ambil data kwitansi:', err);
      alert(t('Gagal memuat data kwitansi.'));
    }
  };

  useEffect(() => {
    if (!isAuthenticated()) return;

    const fetchInitialData = async () => {
      await fetchKwitansi(); 

      try {
        const profilRes = await getProfil(); 
        const profilData = profilRes.data?.data;
        if (Array.isArray(profilData)) {
          setProfilList(profilData);
        } else {
          console.warn('⚠️ Data profil bukan array:', profilData);
          setProfilList([]);
        }
      } catch (err) {
        console.error('Gagal ambil data profil:', err);
        alert(t('Gagal memuat data profil.'));
      }
    };
    
    fetchInitialData();
  }, [isAuthenticated]); 


  const handleCreate = async (data) => {
    try {
      await createKwitansi(data);
      await fetchKwitansi(); 
      setEditingKwitansi(null); 
    } catch (err) {
      console.error('Gagal tambah kwitansi:', err);
      alert(t('Gagal menambahkan data kwitansi.'));
    }
  };

  const handleUpdate = async (id, data) => {
    try {
      await updateKwitansi(id, data);
      await fetchKwitansi(); 
      setEditingKwitansi(null); 
    } catch (err) {
      console.error('Gagal update kwitansi:', err);
      alert(t('Gagal memperbarui data kwitansi.'));
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm(t('Apakah Anda yakin ingin menghapus data ini?'))) {
      try {
        await deleteKwitansi(id);
        await fetchKwitansi(); 
      } catch (err) {
        console.error('Gagal hapus kwitansi:', err);
        alert(t('Gagal menghapus data kwitansi.'));
      }
    }
  };

  const handleEdit = (kwitansi) => {
    const formattedKwitansi = {
        ...kwitansi,
        tanggal: kwitansi.tanggal ? new Date(kwitansi.tanggal).toISOString().split('T')[0] : '',
    };
    setEditingKwitansi(formattedKwitansi);
  };
  
  const handleCancelEdit = () => {
    setEditingKwitansi(null);
  };

  const handleEntriesChange = (e) => {
    setEntriesPerPage(Number(e.target.value));
    setCurrentPage(1);
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h1 className="text-2xl font-bold mb-6">{t('Data Kwitansi')}</h1>
        <div className="flex justify-between items-center mb-6">
          <button
            onClick={() =>
              setEditingKwitansi({
                id: null,
                profil_id: '',
                untuk: '',
                tanggal: new Date().toISOString().split('T')[0], // Default tanggal hari ini
                jumlah: '',
              })
            }
            className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          >
            {t('Tambah Kwitansi')}
          </button>
          <input
            type="text"
            placeholder={t('Search')}
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="border p-2 rounded-lg"
          />
        </div>

        <KwitansiList
          kwitansiList={kwitansiList}
          searchTerm={searchTerm}
          currentPage={currentPage}
          entriesPerPage={entriesPerPage}
          onPageChange={setCurrentPage}
          onEntriesChange={handleEntriesChange}
          onEdit={handleEdit}
          onDelete={handleDelete}
        />
      </div>

      {editingKwitansi && (
        <KwitansiForm
          formData={editingKwitansi}
          setFormData={setEditingKwitansi}
          onSubmit={(data) => {
            if (editingKwitansi.id) {
              handleUpdate(editingKwitansi.id, data);
            } else {
              handleCreate(data);
            }
          }}
          onCancel={handleCancelEdit}
          isEditing={!!editingKwitansi.id}
          profilList={profilList} 
        />
      )}
    </div>
  );
}

export default Kwitansi;
