import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useTranslation } from '../hooks/useTranslation';

function Register() {
  const [nama, setNama] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [noTelp, setNoTelp] = useState('');
  const [alamat, setAlamat] = useState('');
  const [kode, setKode] = useState('');
  const [logo, setLogo] = useState(null);
  const [roleId, setRoleId] = useState(2);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const { t } = useTranslation();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setMessage('');
    setLoading(true);

      const formData = new FormData();
      formData.append('nama', nama.trim());
      formData.append('email', email);
      formData.append('password', password);
      formData.append('no_telp', noTelp);
      formData.append('alamat', alamat);
      formData.append('kode', kode);
      formData.append('role_id', roleId);
      if (logo) formData.append('logo', logo);

    try {
      const response = await axios.post('http://localhost:5000/api/auth/register', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
        withCredentials: true,
      });

      if (response.data.success) {
        setMessage(response.data.message || t('registerSuccess'));
        setTimeout(() => navigate('/login'), 1500);
      } else {
        setError(response.data.message || t('registerFailed'));
      }
    } catch (err) {
      setError(err.response?.data?.message || t('connectionError'));
    } finally {
      setLoading(false);
    }
  };

  const handleLogoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const validTypes = ['image/jpeg', 'image/png', 'image/jpg'];
      if (!validTypes.includes(file.type)) {
        setError(t('invalidImageFormat'));
        return;
      }
      if (file.size > 2 * 1024 * 1024) {
        setError(t('fileTooLarge'));
        return;
      }
      setLogo(file);
      setError('');
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100 pt-20">
      <div className="w-full max-w-md p-6 bg-white rounded-lg shadow-md">
        <h1 className="text-2xl font-bold text-center mb-4">{t('register')}</h1>
        {error && <p className="text-red-500 text-center mb-2">{error}</p>}
        {message && <p className="text-green-500 text-center mb-2">{message}</p>}
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <input
              type="text"
              placeholder={t('name')}
              value={nama}
              onChange={(e) => setNama(e.target.value)}
              autoComplete="name"
              required
              className="w-full px-4 py-2 border rounded"
            />
          </div>
          <div>
            <input
              type="email"
              placeholder={t('email')}
              autoComplete="username"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              className="w-full px-4 py-2 border rounded"
            />
          </div>
          <div>
            <input
              type="password"
              placeholder={t('password')}
              autoComplete="new-password" 
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              className="w-full px-4 py-2 border rounded"
            />
          </div>
          <div>
            <input
              type="tel"
              placeholder={t('phone')}
              value={noTelp}
              onChange={(e) => setNoTelp(e.target.value)}
              autoComplete="tel"
              required
              className="w-full px-4 py-2 border rounded"
            />
          </div>
          <div>
            <textarea
              placeholder={t('address')}
              value={alamat}
              onChange={(e) => setAlamat(e.target.value)}
              autoComplete="street-address"
              rows="3"
              className="w-full px-4 py-2 border rounded"
            />
          </div>
          <div>
            <input
              type="text"
              placeholder={t('code')}
              value={kode}
              onChange={(e) => setKode(e.target.value)}
              autoComplete="off"
              required
              className="w-full px-4 py-2 border rounded"
            />
          </div>
          <div>
            <input
              type="file"
              accept="image/*"
              onChange={handleLogoChange}
              autoComplete="off"
              className="w-full px-4 py-2 border rounded"
            />
          </div>
          <div>
            <select
              value={roleId}
              onChange={(e) => setRoleId(e.target.value)}
              className="w-full px-4 py-2 border rounded"
            >
              <option value="2">{t('role')} 2</option>
              <option value="3">{t('role')} 3</option>
            </select>
          </div>
          <div>
            <button
              type="submit"
              disabled={loading}
              className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700"
            >
              {loading ? t('registering') : t('register')}
            </button>
          </div>
      </form>
        <div className="text-center text-sm text-gray-500 mt-4 flex justify-center items-center gap-x-1">
            <span>{t('alreadyHaveAccount')}</span>
            <button onClick={() => navigate('/login')} className="text-blue-600 hover:underline">
                {t('login')}
            </button>
        </div>
      </div>
    </div>
  );
}

export default Register;
