// frontend/src/pages/InvoiceDetailPages.js

import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { QRCodeSVG } from 'qrcode.react'; 
import { getInvoiceById } from '../api'; 

const InvoiceDetailPages = () => {
    const [invoice, setInvoice] = useState(null);
    const [loading, setLoading] = useState(true);
    const { id } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        const fetchInvoiceDetail = async () => {
            try {
                const response = await getInvoiceById(id);
                setInvoice(response.data.data); 

            } catch (error) {
                console.error("Gagal mengambil detail invoice:", error);
            } finally {
                setLoading(false);
            }
        };
        fetchInvoiceDetail();
    }, [id]);

    const handlePrint = () => {
        window.print();
    };

    if (loading) return <div className="p-6 text-center">Memuat dokumen...</div>;
    if (!invoice) return <div className="p-6 text-center">Dokumen tidak ditemukan.</div>;

    const qrCodeUrl = window.location.href;
    const backgroundImageUrl = invoice.profil ? invoice.profil.backgroundUrl : '';

    return (
        <div className="document-container" style={{ backgroundImage: `url(${backgroundImageUrl})` }}>
            <div className="action-buttons no-print p-6">
                <button onClick={() => navigate('/invoice')} className="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700">
                    Kembali
                </button>
                <button onClick={handlePrint} className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
                    Print Invoice
                </button>
            </div>

            <div className="document-content p-12">
                <header className="text-center mb-8">
                    <h1 className="text-xl font-bold">{invoice.profil?.nama || 'Nama Perusahaan'}</h1>
                    <p className="text-sm">{invoice.profil?.alamat || 'Alamat Perusahaan'}</p>
                </header>
                
                <main>
                    <p><strong>Nomor:</strong> {invoice.no_invoice}</p>
                    <p><strong>Perihal:</strong> {invoice.perihal}</p>
                    <hr className="my-4" />
                    <div dangerouslySetInnerHTML={{ __html: invoice.isi }} />
                </main>

                <div className="signature-section mt-20 flex justify-between items-end">
                    <div className="qr-code-container">
                        <QRCodeSVG value={qrCodeUrl} size={80} />
                    </div>
                    <div className="signature-text text-center">
                        <p>Malang, {new Date(invoice.tanggal).toLocaleDateString('id-ID', { day: 'numeric', month: 'long', year: 'numeric' })}</p>
                        <br /><br /><br />
                        <p className="font-bold underline">Nama Penandatangan</p>
                        <p>Jabatan</p>
                    </div>
                </div>
            </div>

            <footer className="document-footer text-center text-xs text-gray-500 p-6 border-t mt-8">
                <p>Dokumen ini diterbitkan oleh CV MALIKI EDULOGI NUSANTARA dan bersifat asli.</p>
            </footer>
        </div>
    );
};

export default InvoiceDetailPages;
