import React, { useState, useEffect } from 'react';
import { useTranslation } from '../hooks/useTranslation';
import { numberToWords } from '../utils/numberToWords';

function InvoiceForm({ formData, setFormData, onSubmit, onCancel, isEditing, profil = [] }) {
  const { t } = useTranslation();
  const [items, setItems] = useState(formData.items || []);

  useEffect(() => {
    setItems(formData.items || []);
  }, [formData.items]);

  const subtotal = items.reduce((sum, item) => sum + ((Number(item.qty) || 0) * (Number(item.harga) || 0)), 0);
  const ppn_jumlah = subtotal * ((Number(formData.ppn_persen) || 0) / 100);
  const pph_jumlah = subtotal * ((Number(formData.pph_persen) || 0) / 100);
  const total_bayar = subtotal + ppn_jumlah - pph_jumlah + (Number(formData.ongkir) || 0) + (Number(formData.pembulatan) || 0);
  const terbilang = numberToWords(total_bayar);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleItemChange = (index, e) => {
    const newItems = [...items];
    const { name, value } = e.target;
    newItems[index][name] = value;
    setItems(newItems);
  };

  const handleAddItem = () => {
    setItems([...items, { produk: '', deskripsi: '', qty: 1, harga: '' }]);
  };

  const handleRemoveItem = (index) => {
    const newItems = items.filter((_, i) => i !== index);
    setItems(newItems);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.profil_id || !formData.tanggal || items.length === 0) {
      alert(t('Semua field wajib diisi dan setidaknya ada satu item produk.'));
      return;
    }

    const cleanedItems = items.map(item => ({
      ...item,
      qty: Number(item.qty) || 0,
      harga: Number(item.harga) || 0,
    }));

    const dataToSend = {
      ...formData,
      profil_id: Number(formData.profil_id),
      ppn_persen: Number(formData.ppn_persen) || 0,
      pph_persen: Number(formData.pph_persen) || 0,
      ongkir: Number(formData.ongkir) || 0,
      pembulatan: Number(formData.pembulatan) || 0,
      items: cleanedItems,
      subtotal: subtotal,
      ppn_jumlah: ppn_jumlah,
      pph_jumlah: pph_jumlah,
      total_bayar: total_bayar,
      terbilang: terbilang
    };

    onSubmit(dataToSend);
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 overflow-y-auto p-4">
      <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-2xl my-8">
        <h3 className="text-xl font-bold mb-4">{isEditing ? t('Edit Invoice') : t('Tambah Invoice')}</h3>
        <form onSubmit={handleSubmit}>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="flex flex-col">
              <label htmlFor="no_invoice" className="text-sm font-medium text-gray-700">{t('No. Invoice')}</label>
              <input
                id="no_invoice"
                name="no_invoice"
                type="text"
                placeholder={t('No. Invoice')}
                value={formData.no_invoice || ''}
                onChange={handleChange}
                required
                className="mt-1 block w-full border p-2 rounded-md"
              />
            </div>
            <div className="flex flex-col">
              <label htmlFor="tanggal" className="text-sm font-medium text-gray-700">{t('Tanggal')}</label>
              <input
                id="tanggal"
                name="tanggal"
                type="date"
                value={formData.tanggal || ''}
                onChange={handleChange}
                required
                className="mt-1 block w-full border p-2 rounded-md"
              />
            </div>
            <div className="flex flex-col md:col-span-2">
              <label htmlFor="profil_id" className="text-sm font-medium text-gray-700">{t('Tagihan Untuk')}</label>
              <select
                id="profil_id"
                name="profil_id"
                value={formData.profil_id || ''}
                onChange={handleChange}
                required
                className="mt-1 block w-full border p-2 rounded-md"
              >
                <option value="">{t('Pilih Profil')}</option>
                {profil.map(profil => (
                  <option key={profil.id} value={profil.id}>
                    {profil.nama}
                  </option>
                ))}
              </select>
            </div>
          </div>
          
          <h4 className="text-lg font-bold mt-6 mb-2">{t('Isi Invoice')}</h4>
          <div className="space-y-4">
            {items.map((item, index) => (
              <div key={index} className="border p-4 rounded-lg">
                <div className="flex justify-between items-center mb-2">
                  <h5 className="font-semibold">{t('Item')} {index + 1}</h5>
                  <button type="button" onClick={() => handleRemoveItem(index)} className="text-red-500 hover:text-red-700 text-sm">{t('Hapus')}</button>
                </div>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <input
                    name="produk"
                    placeholder={t('Produk')}
                    value={item.produk}
                    onChange={(e) => handleItemChange(index, e)}
                    required
                    className="col-span-2 border p-2 rounded-md"
                  />
                  <input
                    name="deskripsi"
                    placeholder={t('Deskripsi')}
                    value={item.deskripsi}
                    onChange={(e) => handleItemChange(index, e)}
                    className="col-span-2 border p-2 rounded-md"
                  />
                  <input
                    name="qty"
                    type="number"
                    min="0"
                    step="any"
                    placeholder={t('Qty')}
                    value={item.qty}
                    onChange={(e) => handleItemChange(index, e)}
                    required
                    className="border p-2 rounded-md"
                  />
                  <input
                    name="harga"
                    type="number"
                    min="0"
                    step="any"
                    placeholder={t('Harga')}
                    value={item.harga}
                    onChange={(e) => handleItemChange(index, e)}
                    required
                    className="border p-2 rounded-md"
                  />
                </div>
              </div>
            ))}
          </div>
          <button type="button" onClick={handleAddItem} className="mt-4 px-4 py-2 bg-gray-200 rounded hover:bg-gray-300">
            {t('Tambah Produk')}
          </button>

          <div className="mt-6 border-t pt-4">
            <div className="flex justify-between items-center mb-2">
              <span className="font-bold">{t('Subtotal')}</span>
              <span>Rp {subtotal.toLocaleString('id-ID')}</span>
            </div>
            <div className="flex justify-between items-center mb-2">
              <label htmlFor="ppn_persen">{t('PPN')} (%)</label>
              <input
                id="ppn_persen"
                name="ppn_persen"
                type="number"
                min="0"
                step="any"
                value={formData.ppn_persen || ''}
                onChange={handleChange}
                className="w-24 border p-1 rounded-md text-right"
              />
            </div>
            <div className="flex justify-between items-center mb-2">
              <label htmlFor="pph_persen">{t('PPh')} (%)</label>
              <input
                id="pph_persen"
                name="pph_persen"
                type="number"
                min="0"
                step="any"
                value={formData.pph_persen || ''}
                onChange={handleChange}
                className="w-24 border p-1 rounded-md text-right"
              />
            </div>
            <div className="flex justify-between items-center mb-2">
              <label htmlFor="ongkir">{t('Ongkir')}</label>
              <input
                id="ongkir"
                name="ongkir"
                type="number"
                min="0"
                step="any"
                value={formData.ongkir || ''}
                onChange={handleChange}
                className="w-24 border p-1 rounded-md text-right"
              />
            </div>
            <div className="flex justify-between items-center mb-2">
              <label htmlFor="pembulatan">{t('Pembulatan')}</label>
              <input
                id="pembulatan"
                name="pembulatan"
                type="number"
                min="0"
                step="any"
                value={formData.pembulatan || ''}
                onChange={handleChange}
                className="w-24 border p-1 rounded-md text-right"
              />
            </div>
            <hr className="my-2" />
            <div className="flex justify-between items-center font-bold text-lg">
              <span>{t('Total')}</span>
              <span>Rp {total_bayar.toLocaleString('id-ID')}</span>
            </div>
            <p className="mt-2 text-sm text-gray-600">{t('Terbilang:')} {terbilang} Rupiah</p>
          </div>

          <div className="flex justify-end mt-4">
            <button
              type="button"
              onClick={onCancel}
              className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600 mr-2"
            >
              {t('Batal')}
            </button>
            <button
              type="submit"
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              {isEditing ? t('Simpan') : t('Tambah')}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default InvoiceForm;
