import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../context/AuthContext';
import { Navigate } from 'react-router-dom';
import { getBast, createBast, updateBast, deleteBast, getProfil, getKategori } from '../api';
import BastList from '../components/BastList';
import BastForm from '../components/BastForm';
import { useTranslation } from '../hooks/useTranslation';

function Bast() {
  // KOREKSI: Ambil 'user' dari useAuth untuk diteruskan ke form
  const { isAuthenticated, loading, user } = useAuth();
  const { t } = useTranslation();
  const [bastList, setBastList] = useState([]);
  const [profil, setProfil] = useState([]);
  const [kategori, setKategori] = useState([]);
  const [editingBast, setEditingBast] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [entriesPerPage, setEntriesPerPage] = useState(10);

  if (loading) return <div>{t('Loading...')}</div>;
  if (!isAuthenticated()) return <Navigate to="/login" />;

  // KOREKSI: Buat fungsi refresh data yang bisa dipanggil ulang
  const fetchAllBast = async () => {
    try {
      const res = await getBast();
      setBastList(res.data?.data || []);
    } catch (err) {
      console.error('Gagal refresh BAST:', err);
      alert(t('Gagal menyegarkan data BAST.'));
    }
  };

  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const [bastRes, profilRes, kategoriRes] = await Promise.all([
          getBast(), getProfil(), getKategori()
        ]);
        setBastList(bastRes.data?.data || []);
        setProfil(profilRes.data?.data || []);
        setKategori(kategoriRes.data?.data || []);
      } catch (err) {
        console.error('Gagal memuat data awal BAST:', err);
        alert(t('Gagal memuat data.'));
      }
    };
    fetchInitialData();
  }, []);

  const handleCreate = async (data) => {
    try {
      await createBast(data);
      await fetchAllBast(); // Panggil fungsi refresh
      setEditingBast(null);
    } catch (err) {
      console.error('Gagal tambah BAST:', err);
      alert(t('Gagal menambahkan data BAST.'));
    }
  };

  const handleUpdate = async (id, data) => {
    try {
      await updateBast(id, data);
      await fetchAllBast(); // Panggil fungsi refresh
      setEditingBast(null);
    } catch (err) {
      console.error('Gagal update BAST:', err);
      alert(t('Gagal memperbarui data BAST.'));
    }
  };

  // KOREKSI: Optimistic delete seperti pada Surat.js untuk UI yang lebih responsif
  const handleDelete = useCallback(async (id) => {
    if (window.confirm(t('Apakah Anda yakin ingin menghapus data ini?'))) {
      const originalList = [...bastList];
      const updatedList = bastList.filter(b => b.id !== id);
      setBastList(updatedList); // Update UI terlebih dahulu
      try {
        await deleteBast(id);
      } catch (err) {
        console.error('Gagal hapus BAST:', err);
        alert(t('Gagal menghapus data BAST dari server.'));
        setBastList(originalList); // Kembalikan data jika gagal
      }
    }
  }, [bastList, t]);

  const handleEdit = (bastToEdit) => {
    const formattedBast = {
      ...bastToEdit,
      tanggal: bastToEdit.tanggal ? new Date(bastToEdit.tanggal).toISOString().split('T')[0] : '',
    };
    setEditingBast(formattedBast);
  };

  const handleCancelEdit = () => {
    setEditingBast(null);
  };
  
  const handleEntriesChange = (e) => {
    setEntriesPerPage(Number(e.target.value));
    setCurrentPage(1);
  };

  return (
    <div className="p-6 bg-gray-50 min-h-screen">
      <h1 className="text-2xl font-bold mb-6 text-gray-800">{t('Manajemen BAST')}</h1>
      <div className="flex justify-between items-center mb-6">
        <button
          onClick={() =>
            setEditingBast({
              id: null,
              tanggal: new Date().toISOString().split('T')[0],
              profil_id: '',
              isi: '',
              meta_data: {}, // Pastikan meta_data ada agar form tidak error
            })
          }
          className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 shadow"
        >
          {t('Buat BAST Baru')}
        </button>
        {/* ... Search Input ... */}
      </div>

      <BastList
        bast={bastList}
        searchTerm={searchTerm}
        currentPage={currentPage}
        entriesPerPage={entriesPerPage}
        onPageChange={setCurrentPage}
        onEntriesChange={handleEntriesChange}
        onEdit={handleEdit}
        onDelete={handleDelete}
      />

      {editingBast && (
        <BastForm
          initialData={editingBast}
          onSubmit={(data) => {
            if (editingBast.id) {
              handleUpdate(editingBast.id, data);
            } else {
              handleCreate(data);
            }
          }}
          onCancel={handleCancelEdit}
          isEditing={!!editingBast.id}
          profil={profil}
          kategori={kategori}
          user={user} // <-- KUNCI PERBAIKAN: Teruskan data user ke form
        />
      )}
    </div>
  );
}

export default Bast;
