import React, { useState, useEffect } from 'react';
import { useTranslation } from '../hooks/useTranslation';
import { numberToWords } from '../utils/numberToWords';

function InvoiceForm({ formData, setFormData, onSubmit, onCancel, isEditing, profil = [] }) {
  const { t } = useTranslation();
  const [items, setItems] = useState(formData.items || []);

  useEffect(() => {
    setItems(formData.items || []);
  }, [formData.items]);

  const subtotal = items.reduce((sum, item) => sum + ((Number(item.qty) || 0) * (Number(item.harga) || 0)), 0);
  const ppn_jumlah = subtotal * ((Number(formData.ppn_persen) || 0) / 100);
  const pph_jumlah = subtotal * ((Number(formData.pph_persen) || 0) / 100);
  const total_bayar = subtotal + ppn_jumlah - pph_jumlah + (Number(formData.ongkir) || 0) + (Number(formData.pembulatan) || 0);
  const terbilang = numberToWords(total_bayar);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleItemChange = (index, e) => {
    const newItems = [...items];
    const { name, value } = e.target;
    newItems[index][name] = value;
    setItems(newItems);
  };

  const handleAddItem = () => {
    setItems([...items, { produk: '', deskripsi: '', qty: 1, harga: '' }]);
  };

  const handleRemoveItem = (index) => {
    const newItems = items.filter((_, i) => i !== index);
    setItems(newItems);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.profil_id || !formData.tanggal || items.length === 0) {
      alert(t('Semua field wajib diisi dan setidaknya ada satu item produk.'));
      return;
    }

    const cleanedItems = items.map(item => ({
      ...item,
      qty: Number(item.qty) || 0,
      harga: Number(item.harga) || 0,
    }));

    const dataToSend = {
      ...formData,
      profil_id: Number(formData.profil_id),
      items: cleanedItems,
      subtotal,
      ppn_jumlah,
      pph_jumlah,
      total_bayar,
      terbilang
    };

    onSubmit(dataToSend);
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      {/* ✅ KOREKSI: Struktur modal dibuat flex dan diberi tinggi maksimal */}
      <div className="bg-white rounded-lg shadow-lg w-full max-w-2xl flex flex-col max-h-[90vh]">
        <h3 className="text-xl font-bold p-6 pb-4 flex-shrink-0">
          {isEditing ? t('Edit Invoice') : t('Tambah Invoice')}
        </h3>
        
        {/* ✅ KOREKSI: Form dijadikan area yang bisa di-scroll */}
        <form onSubmit={handleSubmit} className="overflow-y-auto px-6 pb-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {/* ... input no_invoice dan tanggal ... */}
          </div>
          
          <h4 className="text-lg font-bold mt-6 mb-2">{t('Isi Invoice')}</h4>
          <div className="space-y-4">
            {items.map((item, index) => (
              <div key={index} className="border p-4 rounded-lg">
                {/* ... detail item ... */}
              </div>
            ))}
          </div>
          <button type="button" onClick={handleAddItem} className="mt-4 px-4 py-2 bg-gray-200 rounded hover:bg-gray-300">
            {t('Tambah Produk')}
          </button>

          <div className="mt-6 border-t pt-4">
            {/* ... detail pembayaran ... */}
          </div>

          <div className="flex justify-end mt-4 border-t pt-4">
            <button type="button" onClick={onCancel} className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600 mr-2">
              {t('Batal')}
            </button>
            <button type="submit" className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
              {isEditing ? t('Simpan') : t('Tambah')}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default InvoiceForm;
