import React, { useState, useEffect, useMemo, useCallback } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { renderBastTemplate } from '../utils/renderBastTemplate';

function BastForm({ initialData, onSubmit, onCancel, isEditing, profil = [], kategori = [], user }) {
  // === STATE MANAGEMENT ===
  const [formData, setFormData] = useState(initialData);
  const [dynamicFields, setDynamicFields] = useState(initialData.meta_data?.dynamicFields || {});
  const [items, setItems] = useState(initialData.meta_data?.items || []);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentItem, setCurrentItem] = useState({ produk: '', deskripsi: '', qty: 1, harga: '', kondisi: 'Baik' });
  const [originalTemplate, setOriginalTemplate] = useState('');

  // === HANDLERS ===
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleDynamicChange = (e) => {
    setDynamicFields(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleItemFormChange = (e) => {
    setCurrentItem(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSaveItem = () => {
    if (!currentItem.produk || !currentItem.deskripsi) {
      alert('Nama produk dan deskripsi wajib diisi.');
      return;
    }
    setItems([...items, currentItem]);
    setCurrentItem({ produk: '', deskripsi: '', qty: 1, harga: '', kondisi: 'Baik' });
    setIsModalOpen(false);
  };

  const handleRemoveItem = (indexToRemove) => {
    setItems(items.filter((_, index) => index !== indexToRemove));
  };

  // === EFEK UNTUK REAL-TIME PREVIEW ===
  useEffect(() => {
    const bastKategori = kategori.find(k => k.kode === 'BAST');
    if (bastKategori) {
      setOriginalTemplate(bastKategori.template || '');
      if (!isEditing) {
        setFormData(prev => ({ ...prev, perihal: bastKategori.nama_kategori || 'Berita Acara Serah Terima' }));
      }
    }
  }, [kategori, isEditing]);

  useEffect(() => {
    if (!originalTemplate) return;
    const newContent = renderBastTemplate(originalTemplate, formData, dynamicFields, items, user, profil);
    if (newContent !== formData.isi) {
      setFormData(prev => ({ ...prev, isi: newContent }));
    }
  }, [originalTemplate, formData, dynamicFields, items, user, profil]);

  useEffect(() => {
    setFormData(initialData);
    setDynamicFields(initialData.meta_data?.dynamicFields || {});
    setItems(initialData.meta_data?.items || []);
  }, [initialData]);

  // === SUBMIT HANDLER ===
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.profil_id || !formData.tanggal) {
      alert('Tujuan dan Tanggal wajib diisi.');
      return;
    }
    const dataToSend = {
      ...formData,
      meta_data: { dynamicFields, items }
    };
    onSubmit(dataToSend);
  };

  const modules = useMemo(() => ({
    toolbar: [
      [{ 'header': [1, 2, 3, false] }],
      ['bold', 'italic', 'underline', 'strike'],
      [{'list': 'ordered'}, {'list': 'bullet'}],
      ['link', 'image', 'table'],
    ],
  }), []);

  // === RENDER JSX ===
  return (
    <>
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
        <div className="bg-white rounded-lg shadow-lg w-full max-w-4xl flex flex-col max-h-[95vh]">
          <h3 className="text-xl font-bold p-6 pb-4 flex-shrink-0">{isEditing ? 'Edit BAST' : 'Tambah BAST'}</h3>
          <div className="flex-grow overflow-y-auto px-6 pb-6">
            <form onSubmit={handleSubmit} className="space-y-4">
              {/* Input Fields (Tanggal & Profil) */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="tanggal" className="block text-sm font-medium text-gray-700">Tanggal BAST</label>
                  <input id="tanggal" name="tanggal" type="date" value={formData.tanggal || ''} onChange={handleChange} required className="mt-1 w-full border p-2 rounded-md shadow-sm"/>
                </div>
                <div>
                  <label htmlFor="profil_id" className="block text-sm font-medium text-gray-700">Tujuan (Profil Pihak Kedua)</label>
                  <select id="profil_id" name="profil_id" value={formData.profil_id || ''} onChange={handleChange} required className="mt-1 w-full border p-2 rounded-md shadow-sm">
                    <option value="">-- Pilih Profil Tujuan --</option>
                    {profil.map(p => (<option key={p.id} value={p.id}>{p.nama}</option>))}
                  </select>
                </div>
              </div>

              {/* Dynamic Fields (Pihak Terlibat) */}
              <div className="p-4 border-l-4 border-gray-300 bg-gray-50 my-4 rounded-r-lg space-y-4">
                 <h4 className="font-bold text-gray-800 mb-2">Pihak Terlibat</h4>
                 <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                   <input name="nama_penandatangan" value={dynamicFields.nama_penandatangan || ''} onChange={handleDynamicChange} placeholder="Nama Pihak Pertama (Anda)" className="border p-2 rounded-md" />
                   <input name="jabatan_penandatangan" value={dynamicFields.jabatan_penandatangan || ''} onChange={handleDynamicChange} placeholder="Jabatan Pihak Pertama (Anda)" className="border p-2 rounded-md" />
                 </div>
              </div>

              {/* Detail Produk */}
              <div className="p-4 border-l-4 border-blue-300 bg-blue-50 my-4 rounded-r-lg space-y-4">
                <h4 className="font-bold text-gray-800 mb-2">Detail Produk/Jasa yang Diserahkan</h4>
                <div>
                  <button type="button" onClick={() => setIsModalOpen(true)} className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
                    + Tambah Produk
                  </button>
                </div>
                <div className="space-y-2 mt-2">
                  {items.map((item, index) => (
                    <div key={index} className="flex items-center gap-2 p-2 bg-blue-100 rounded">
                      <span className="font-semibold">{index + 1}.</span>
                      <span className="flex-grow">{item.produk} - {item.deskripsi} (Qty: {item.qty})</span>
                      <button type="button" onClick={() => handleRemoveItem(index)} className="text-red-500 font-bold hover:text-red-700">X</button>
                    </div>
                  ))}
                </div>
              </div>
              
              {/* ReactQuill Editor */}
              <div>
                <label className="block text-sm font-medium text-gray-700">Isi Berita Acara (Preview)</label>
                <ReactQuill theme="snow" value={formData.isi || ''} onChange={(content) => setFormData(prev => ({ ...prev, isi: content }))} modules={modules} className="mt-1"/>
              </div>

              {/* Action Buttons */}
              <div className="flex justify-end pt-4 gap-2 border-t mt-4">
                <button type="button" onClick={onCancel} className="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700">Batal</button>
                <button type="submit" className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700">{isEditing ? 'Simpan Perubahan' : 'Buat BAST'}</button>
              </div>
            </form>
          </div>
        </div>
      </div>

      {/* Modal Tambah Produk */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-[60]">
          <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-lg space-y-4">
            <h3 className="text-xl font-bold">Tambah Detail Produk/Jasa</h3>
            <input name="produk" value={currentItem.produk} onChange={handleItemFormChange} placeholder="Nama Produk/Jasa" className="w-full border p-2 rounded" />
            <input name="deskripsi" value={currentItem.deskripsi} onChange={handleItemFormChange} placeholder="Deskripsi Singkat" className="w-full border p-2 rounded" />
            <div className="grid grid-cols-3 gap-4">
              <input name="qty" type="number" min="1" value={currentItem.qty} onChange={handleItemFormChange} placeholder="Qty" className="w-full border p-2 rounded" />
              <input name="harga" type="number" min="0" value={currentItem.harga} onChange={handleItemFormChange} placeholder="Total Harga (Opsional)" className="w-full border p-2 rounded" />
              <input name="kondisi" value={currentItem.kondisi} onChange={handleItemFormChange} placeholder="Kondisi" className="w-full border p-2 rounded" />
            </div>
            <div className="flex justify-end gap-2">
              <button type="button" onClick={() => setIsModalOpen(false)} className="px-4 py-2 bg-gray-600 text-white rounded">Batal</button>
              <button type="button" onClick={handleSaveItem} className="px-4 py-2 bg-blue-600 text-white rounded">Simpan Item</button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default BastForm;
