import React, { useState, useEffect, useMemo } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

function BastForm({ initialData, onSubmit, onCancel, isEditing, profil = [], kategori = [] }) {
  const [formData, setFormData] = useState(initialData);

  useEffect(() => {
    setFormData(initialData);
  }, [initialData]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleContentChange = (value) => {
    setFormData(prevData => ({ ...prevData, isi: value }));
  };

  useEffect(() => {
    if (!isEditing) {
      const bastKategori = kategori.find(k => k.kode === 'BAST');
      if (bastKategori) {
        setFormData(prevData => ({
          ...prevData,
          isi: bastKategori.template || '',
          perihal: bastKategori.nama_kategori || 'Berita Acara Serah Terima',
        }));
      }
    }
  }, [isEditing, kategori]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.profil_id || !formData.tanggal || !formData.nama_pihak_terlibat) {
        alert('Tujuan, Tanggal, dan Nama Pihak Terlibat wajib diisi.');
        return;
    }
    onSubmit(formData);
  };
  
  // âœ… KOREKSI 1: Konfigurasi modul yang benar untuk Quill v2
  const modules = useMemo(() => ({
    toolbar: [
      [{ 'header': [1, 2, 3, false] }],
      ['bold', 'italic', 'underline', 'strike', 'blockquote'],
      [{ 'list': 'ordered'}, { 'list': 'bullet' }],
      ['link', 'image', 'table'], // Tombol tabel akan muncul
    ],
  }), []);

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      {/* âœ… KOREKSI 2: Struktur modal dibuat flex dan diberi tinggi maksimal */}
      <div className="bg-white rounded-lg shadow-lg w-full max-w-2xl flex flex-col max-h-[90vh]">
        <h3 className="text-xl font-bold p-6 pb-4 flex-shrink-0">
          {isEditing ? 'Edit BAST' : 'Tambah BAST'}
        </h3>
        
        {/* âœ… KOREKSI 3: Form dijadikan area yang bisa di-scroll */}
        <form onSubmit={handleSubmit} className="space-y-4 overflow-y-auto px-6 pb-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label htmlFor="profil_id" className="block text-sm font-medium">Tujuan (Profil)</label>
              <select id="profil_id" name="profil_id" value={formData.profil_id || ''} onChange={handleChange} required className="mt-1 w-full border p-2 rounded-md">
                <option value="">-- Pilih Profil --</option>
                {profil.map(p => (<option key={p.id} value={p.id}>{p.nama}</option>))}
              </select>
            </div>
            <div>
              <label htmlFor="tanggal" className="block text-sm font-medium">Tanggal BAST</label>
              <input id="tanggal" name="tanggal" type="date" value={formData.tanggal || ''} onChange={handleChange} required className="mt-1 w-full border p-2 rounded-md" />
            </div>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label htmlFor="nama_pihak_terlibat" className="block text-sm font-medium">Nama Pihak Terlibat</label>
              <input id="nama_pihak_terlibat" name="nama_pihak_terlibat" value={formData.nama_pihak_terlibat || ''} onChange={handleChange} required className="mt-1 w-full border p-2 rounded-md" />
            </div>
            <div>
              <label htmlFor="jabatan_pihak_terlibat" className="block text-sm font-medium">Jabatan Pihak Terlibat</label>
              <input id="jabatan_pihak_terlibat" name="jabatan_pihak_terlibat" value={formData.jabatan_pihak_terlibat || ''} onChange={handleChange} required className="mt-1 w-full border p-2 rounded-md" />
            </div>
          </div>
          <div>
            <label className="block text-sm font-medium">Isi Berita Acara</label>
            <ReactQuill
              theme="snow"
              value={formData.isi || ''}
              onChange={handleContentChange}
              modules={modules} // Gunakan modul yang sudah dikoreksi
              className="mt-1"
            />
          </div>
          <div className="flex justify-end pt-4 gap-2 border-t mt-4">
            <button type="button" onClick={onCancel} className="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700">Batal</button>
            <button type="submit" className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">{isEditing ? 'Simpan Perubahan' : 'Buat BAST'}</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default BastForm;
