import React, { useState, useEffect, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import { getArsipFiles, uploadArsipFile, deleteArsipFile } from '../api';

function ArsipPages() {
  const { kategori } = useParams(); // 'Faktur', 'Billing', atau 'Bukti Pembayaran'
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');

  // State untuk form upload
  const [judul, setJudul] = useState('');
  const [fileToUpload, setFileToUpload] = useState(null);
  const [uploading, setUploading] = useState(false);

  const fetchFiles = useCallback(async () => {
    setLoading(true);
    try {
      const response = await getArsipFiles(kategori, searchTerm);
      setFiles(response.data.data);
    } catch (error) {
      console.error(`Gagal memuat ${kategori}:`, error);
      alert(`Gagal memuat data ${kategori}`);
    } finally {
      setLoading(false);
    }
  }, [kategori, searchTerm]);

  useEffect(() => {
    fetchFiles();
  }, [fetchFiles]);

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!fileToUpload || !judul) {
      alert('Judul dan file wajib diisi.');
      return;
    }
    setUploading(true);
    const formData = new FormData();
    formData.append('file', fileToUpload);
    formData.append('judul', judul);
    formData.append('kategori', kategori);

    try {
      await uploadArsipFile(formData);
      alert('File berhasil diupload!');
      setJudul('');
      setFileToUpload(null);
      document.getElementById('file-input').value = ''; // Reset input file
      fetchFiles(); // Refresh list
    } catch (error) {
      console.error('Gagal upload:', error);
      alert('Gagal mengupload file.');
    } finally {
      setUploading(false);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Apakah Anda yakin ingin menghapus file ini?')) {
      try {
        await deleteArsipFile(id);
        alert('File berhasil dihapus.');
        fetchFiles(); // Refresh list
      } catch (error) {
        console.error('Gagal hapus:', error);
        alert('Gagal menghapus file.');
      }
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">Manajemen {kategori}</h1>

      {/* Form Upload */}
      <div className="bg-white p-4 rounded-lg shadow-md mb-6">
        <h2 className="text-xl font-semibold mb-4">Upload {kategori} Baru</h2>
        <form onSubmit={handleUpload} className="space-y-4">
          <div>
            <label htmlFor="judul" className="block text-sm font-medium text-gray-700">Judul File</label>
            <input type="text" id="judul" value={judul} onChange={(e) => setJudul(e.target.value)} required className="mt-1 w-full border p-2 rounded-md" />
          </div>
          <div>
            <label htmlFor="file-input" className="block text-sm font-medium text-gray-700">Pilih File</label>
            <input type="file" id="file-input" onChange={(e) => setFileToUpload(e.target.files[0])} required className="mt-1 w-full border p-2 rounded-md" />
          </div>
          <button type="submit" disabled={uploading} className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 disabled:bg-gray-400">
            {uploading ? 'Mengupload...' : 'Upload'}
          </button>
        </form>
      </div>

      {/* Daftar File */}
      <div className="bg-white p-4 rounded-lg shadow-md">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-xl font-semibold">Daftar {kategori}</h2>
          <input type="text" placeholder="Search..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} className="w-1/3 border p-2 rounded-md" />
        </div>
        <div className="overflow-x-auto">
          <table className="w-full table-auto">
            <thead className="bg-gray-100">
              <tr>
                <th className="px-4 py-2 text-left">Judul</th>
                <th className="px-4 py-2 text-left">Nama File</th>
                <th className="px-4 py-2 text-left">Tanggal Upload</th>
                <th className="px-4 py-2 text-left">Aksi</th>
              </tr>
            </thead>
            <tbody className="divide-y">
              {loading ? (
                <tr><td colSpan="4" className="text-center py-4">Loading...</td></tr>
              ) : files.length === 0 ? (
                <tr><td colSpan="4" className="text-center py-4">Tidak ada data.</td></tr>
              ) : (
                files.map(file => (
                  <tr key={file.id}>
                    <td className="px-4 py-2">{file.judul}</td>
                    <td className="px-4 py-2">{file.nama_file}</td>
                    <td className="px-4 py-2">{new Date(file.created_at).toLocaleDateString('id-ID')}</td>
                    <td className="px-4 py-2">
                      <button onClick={() => handleDelete(file.id)} className="text-red-500 hover:text-red-700">
                        Hapus
                      </button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default ArsipPages;
