import React, { useState, useEffect } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

// ✅ DIUBAH: Terima `initialData` sebagai prop utama
function SuratForm({ initialData, onSubmit, onCancel, isEditing, profil = [], kategori = [] }) {
  
  // ✅ BUAT STATE LOKAL: Form ini sekarang mengontrol datanya sendiri
  const [formData, setFormData] = useState(initialData);

  /**
   * Hook ini menyinkronkan state lokal jika data dari induk berubah
   * (misalnya, saat pengguna menutup modal edit satu surat dan membuka surat lain).
   */
  useEffect(() => {
    setFormData(initialData);
  }, [initialData]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleContentChange = (value) => {
    setFormData(prevData => ({ ...prevData, isi: value }));
  };

  // Logika untuk memuat template tetap sama
  useEffect(() => {
    if (!isEditing && formData.kategori_kode) {
      const selectedKategori = kategori.find(k => k.kode === formData.kategori_kode);
      if (selectedKategori) {
        setFormData(prevData => ({
          ...prevData,
          isi: selectedKategori.template || '',
          perihal: selectedKategori.nama_kategori || '',
        }));
      }
    }
  }, [formData.kategori_kode, isEditing, kategori]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.profil_id || !formData.kategori_kode || !formData.tanggal) {
      alert('Tujuan, Kategori, dan Tanggal wajib diisi.');
      return;
    }
    // ✅ Kirim data final ke fungsi onSubmit yang diberikan oleh induk
    onSubmit(formData);
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-2xl my-8">
        <h3 className="text-xl font-bold mb-4">{isEditing ? 'Edit Surat' : 'Tambah Surat'}</h3>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label htmlFor="tanggal" className="block text-sm font-medium">Tanggal Surat</label>
              <input id="tanggal" name="tanggal" type="date" value={formData.tanggal || ''} onChange={handleChange} required className="mt-1 w-full border p-2 rounded-md" />
            </div>
            <div>
              <label htmlFor="profil_id" className="block text-sm font-medium">Tujuan Surat</label>
              <select id="profil_id" name="profil_id" value={formData.profil_id || ''} onChange={handleChange} required className="mt-1 w-full border p-2 rounded-md">
                <option value="">-- Pilih Profil Tujuan --</option>
                {profil.map(p => (<option key={p.id} value={p.id}>{p.nama}</option>))}
              </select>
            </div>
          </div>
          <div>
            <label htmlFor="kategori_kode" className="block text-sm font-medium">Kategori</label>
            <select id="kategori_kode" name="kategori_kode" value={formData.kategori_kode || ''} onChange={handleChange} required disabled={isEditing} className="mt-1 w-full border p-2 rounded-md">
              <option value="">-- Pilih Kategori Surat --</option>
              {kategori.map(k => (<option key={k.kode} value={k.kode}>{k.nama_kategori} ({k.kode})</option>))}
            </select>
          </div>
          <div>
            <label htmlFor="isi" className="block text-sm font-medium">Isi Surat</label>
            <ReactQuill
              theme="snow"
              // ✅ SELALU AMAN: `formData.isi` dijamin ada karena state lokal
              value={formData.isi || ''}
              onChange={handleContentChange}
              className="mt-1"
            />
          </div>
          <div className="flex justify-end pt-4 gap-2">
            <button type="button" onClick={onCancel} className="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700">Batal</button>
            <button type="submit" className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">{isEditing ? 'Simpan Perubahan' : 'Buat Surat'}</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default SuratForm;
