import React, { useState, useEffect, useMemo, useCallback } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { renderBastTemplate } from '../utils/renderBastTemplate'; // Menggunakan kembali renderer yang sama

// BARU: Terima prop 'user' untuk mengakses data user yang login
function BastForm({ initialData, onSubmit, onCancel, isEditing, profil = [], kategori = [], user }) {
  // === STATE MANAGEMENT (Mengikuti pola SuratForm) ===
  const [formData, setFormData] = useState(initialData);
  // BARU: State untuk field dinamis seperti nama & jabatan pihak terlibat
  const [dynamicFields, setDynamicFields] = useState(initialData.meta_data?.dynamicFields || {});
  // BARU: State untuk menyimpan template asli dari kategori
  const [originalTemplate, setOriginalTemplate] = useState('');

  // === HANDLERS ===
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // BARU: Handler untuk field dinamis
  const handleDynamicChange = (e) => {
    setDynamicFields(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  // === EFEK UNTUK REAL-TIME PREVIEW ===

  // 1. Mengambil template asli saat komponen dimuat (untuk BAST)
  useEffect(() => {
    const bastKategori = kategori.find(k => k.kode === 'BAST');
    if (bastKategori) {
      setOriginalTemplate(bastKategori.template || '');
      // Set perihal jika belum ada (saat membuat baru)
      if (!isEditing) {
        setFormData(prev => ({
          ...prev,
          perihal: bastKategori.nama_kategori || 'Berita Acara Serah Terima',
        }));
      }
    }
  }, [kategori, isEditing]);

  // 2. Merender ulang template setiap kali ada perubahan pada data terkait
  useEffect(() => {
    // Jangan render jika template belum siap
    if (!originalTemplate) return;

    // Panggil utilitas render dengan data saat ini
    const newContent = renderBastTemplate(originalTemplate, formData, dynamicFields, [], user, profil);
    
    // Perbarui 'isi' surat hanya jika kontennya benar-benar berubah untuk mencegah loop tak terbatas
    if (newContent !== formData.isi) {
      setFormData(prev => ({ ...prev, isi: newContent }));
    }
  }, [originalTemplate, formData, dynamicFields, user, profil]); // <-- dependensi yang memicu render ulang

  // 3. Reset state saat initialData berubah (saat membuka form untuk edit)
  useEffect(() => {
    setFormData(initialData);
    setDynamicFields(initialData.meta_data?.dynamicFields || {});
  }, [initialData]);

  // === SUBMIT HANDLER ===
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.profil_id || !formData.tanggal || !dynamicFields.nama_pihak_terlibat) {
      alert('Tujuan, Tanggal, dan Nama Pihak Terlibat wajib diisi.');
      return;
    }
    // BARU: Gabungkan dynamicFields ke dalam meta_data saat submit
    const dataToSend = {
      ...formData,
      meta_data: { dynamicFields }
    };
    onSubmit(dataToSend);
  };

  const modules = useMemo(() => ({
    toolbar: [
      [{ 'header': [1, 2, 3, false] }],
      ['bold', 'italic', 'underline', 'strike'],
      [{'list': 'ordered'}, {'list': 'bullet'}],
      ['link', 'image', 'table'],
    ],
  }), []);

  // === RENDER JSX ===
  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg shadow-lg w-full max-w-4xl flex flex-col max-h-[95vh]">
        <h3 className="text-xl font-bold p-6 pb-4 flex-shrink-0">
          {isEditing ? 'Edit BAST' : 'Tambah BAST'}
        </h3>
        
        <div className="flex-grow overflow-y-auto px-6 pb-6">
          <form onSubmit={handleSubmit} className="space-y-4">
            {/* Input Fields */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label htmlFor="tanggal" className="block text-sm font-medium text-gray-700">Tanggal BAST</label>
                <input id="tanggal" name="tanggal" type="date" value={formData.tanggal || ''} onChange={handleChange} required className="mt-1 w-full border p-2 rounded-md shadow-sm"/>
              </div>
              <div>
                <label htmlFor="profil_id" className="block text-sm font-medium text-gray-700">Tujuan (Profil)</label>
                <select id="profil_id" name="profil_id" value={formData.profil_id || ''} onChange={handleChange} required className="mt-1 w-full border p-2 rounded-md shadow-sm">
                  <option value="">-- Pilih Profil Tujuan --</option>
                  {profil.map(p => (<option key={p.id} value={p.id}>{p.nama}</option>))}
                </select>
              </div>
            </div>

            {/* Dynamic Fields Section */}
            <div className="p-4 border-l-4 border-gray-300 bg-gray-50 my-4 rounded-r-lg space-y-4">
              <h4 className="font-bold text-gray-800 mb-2">Pihak Terlibat</h4>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {/* KOREKSI: Hubungkan input ke 'dynamicFields' state */}
                <input name="nama_pihak_terlibat" value={dynamicFields.nama_pihak_terlibat || ''} onChange={handleDynamicChange} placeholder="Nama Pihak Pertama" className="border p-2 rounded-md" />
                <input name="jabatan_pihak_terlibat" value={dynamicFields.jabatan_pihak_terlibat || ''} onChange={handleDynamicChange} placeholder="Jabatan Pihak Pertama" className="border p-2 rounded-md" />
                <input name="nama_pihak_kedua" value={dynamicFields.nama_pihak_kedua || ''} onChange={handleDynamicChange} placeholder="Nama Pihak Kedua" className="border p-2 rounded-md" />
                <input name="jabatan_pihak_kedua" value={dynamicFields.jabatan_pihak_kedua || ''} onChange={handleDynamicChange} placeholder="Jabatan Pihak Kedua" className="border p-2 rounded-md" />
              </div>
            </div>
            
            {/* ReactQuill Editor */}
            <div>
              <label className="block text-sm font-medium text-gray-700">Isi Berita Acara (Preview)</label>
              <ReactQuill
                theme="snow"
                value={formData.isi || ''}
                onChange={(content) => setFormData(prev => ({ ...prev, isi: content }))}
                modules={modules}
                className="mt-1 bg-gray-100"
              />
            </div>

            {/* Action Buttons */}
            <div className="flex justify-end pt-4 gap-2 border-t mt-4">
              <button type="button" onClick={onCancel} className="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700">Batal</button>
              <button type="submit" className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700">{isEditing ? 'Simpan Perubahan' : 'Buat BAST'}</button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default BastForm;
