import React, { useEffect, useState } from 'react';

// Props 'profilList' akan berisi daftar profil yang diambil dari API
function KwitansiForm({ formData, setFormData, onSubmit, onCancel, isEditing, profilList = [] }) {
  
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Validasi field yang wajib diisi oleh pengguna
    if (!formData.profil_id || !formData.tanggal || !formData.untuk || !formData.jumlah) {
      alert('Semua field wajib diisi!');
      return;
    }

    // Menyiapkan data yang akan dikirim, memastikan tipe data angka sudah benar
    const dataToSend = {
      ...formData,
      profil_id: Number(formData.profil_id),
      jumlah: Number(formData.jumlah),
    };
    
    onSubmit(dataToSend);
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-md">
        <h3 className="text-xl font-bold mb-4">{isEditing ? 'Edit Kwitansi' : 'Tambah Kwitansi'}</h3>
        <form onSubmit={handleSubmit} className="space-y-4">
          
          {/* Input untuk 'profil_id' sebagai Dropdown */}
          <div>
            <label htmlFor="profil_id" className="block text-sm font-medium text-gray-700">Sudah terima dari</label>
            <select
              id="profil_id"
              name="profil_id"
              value={formData.profil_id || ''}
              onChange={handleChange}
              required
              className="mt-1 block w-full border p-2 rounded-md"
            >
              <option value="">--- Pilih Profil ---</option>
              {profilList.map(profil => (
                <option key={profil.id} value={profil.id}>
                  {profil.nama} 
                </option>
              ))}
            </select>
          </div>

          {/* Input untuk 'untuk' sebagai Textarea */}
          <div>
            <label htmlFor="untuk" className="block text-sm font-medium text-gray-700">Untuk Pembayaran</label>
            <textarea
              id="untuk"
              name="untuk"
              placeholder="Contoh: Service dan Ganti Sparepart Printer..."
              value={formData.untuk || ''}
              onChange={handleChange}
              required
              rows="3"
              className="mt-1 block w-full border p-2 rounded-md"
            />
          </div>

          {/* Input untuk 'tanggal' sebagai Date Picker */}
          <div>
            <label htmlFor="tanggal" className="block text-sm font-medium text-gray-700">Tanggal</label>
            <input
              id="tanggal"
              name="tanggal"
              type="date"
              value={formData.tanggal || ''}
              onChange={handleChange}
              required
              className="mt-1 block w-full border p-2 rounded-md"
            />
          </div>

          {/* Input untuk 'jumlah' sebagai Angka */}
          <div>
            <label htmlFor="jumlah" className="block text-sm font-medium text-gray-700">Sebesar (Rp)</label>
            <input
              id="jumlah"
              name="jumlah"
              type="number"
              min="0"
              placeholder="0"
              value={formData.jumlah || ''}
              onChange={handleChange}
              required
              className="mt-1 block w-full border p-2 rounded-md"
            />
          </div>
          
          {/* Tombol Aksi */}
          <div className="flex justify-end pt-4">
            <button
              type="button"
              onClick={onCancel}
              className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600 mr-2"
            >
              Batal
            </button>
            <button
              type="submit"
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              {isEditing ? 'Simpan Perubahan' : 'Simpan'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default KwitansiForm;
