import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { Navigate } from 'react-router-dom';
import { getBast, createBast, updateBast, deleteBast, getProfil, getKategori } from '../api';
import BastList from '../components/BastList';
import BastForm from '../components/BastForm';
import { useTranslation } from '../hooks/useTranslation';

function Bast() {
  const { isAuthenticated, loading } = useAuth();
  const { t } = useTranslation();
  const [bastList, setBastList] = useState([]);
  const [profil, setProfil] = useState([]);
  const [kategori, setKategori] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [entriesPerPage, setEntriesPerPage] = useState(10);
  const [editingBast, setEditingBast] = useState(null);

  if (loading) return <div>{t('Loading...')}</div>;
  if (!isAuthenticated()) return <Navigate to="/login" />;

  useEffect(() => {
    const fetchInitialData = async () => {
      try {
        const [bastRes, profilRes, kategoriRes] = await Promise.all([
          getBast(), getProfil(), getKategori()
        ]);
        setBastList(bastRes.data?.data || []);
        setProfil(profilRes.data?.data || []);
        setKategori(kategoriRes.data?.data || []);
      } catch (err) {
        console.error('Gagal memuat data awal BAST:', err);
        alert(t('Gagal memuat data.'));
      }
    };
    fetchInitialData();
  }, []);

  // ... fungsi handleUpdate dan handleDelete tidak berubah ...
  const handleCreate = async (data) => {
    try {
      await createBast(data);
      const res = await getBast(); 
      setBastList(res.data?.data || []);
      setEditingBast(null);
    } catch (err) {
      console.error('Gagal tambah bast:', err);
      alert(t('Gagal menambahkan data bast.'));
    }
  };

  const handleUpdate = async (id, data) => {
    try {
      await updateBast(id, data);
      const res = await getBast(); 
      setBastList(res.data?.data || []);
      setEditingBast(null);
    } catch (err) {
      console.error('Gagal update bast:', err);
      alert(t('Gagal memperbarui data bast.'));
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm(t('Apakah Anda yakin ingin menghapus data ini?'))) {
      try {
        await deleteBast(id);
        const res = await getBast(); 
        setBastList(res.data?.data || []);
      } catch (err) {
        console.error('Gagal hapus bast:', err);
        alert(t('Gagal menghapus data bast.'));
      }
    }
  };

  /**
   * ✅ FUNGSI BARU: Menyiapkan data untuk form "Create"
   * Fungsi ini akan mencari template BAST dari state 'kategori'
   */
  const handleCreateClick = () => {
    const bastKategori = kategori.find(k => k.kode === 'BAST');
    
    // Siapkan data awal yang lengkap
    const newBastData = {
      id: null,
      tanggal: new Date().toISOString().split('T')[0], // Default tanggal hari ini
      profil_id: '',
      nama_pihak_terlibat: '',
      jabatan_pihak_terlibat: '',
      kategori_kode: 'BAST',
      // Gunakan template jika ditemukan, jika tidak, gunakan string kosong
      isi: bastKategori?.template || '',
      perihal: bastKategori?.nama_kategori || 'Berita Acara Serah Terima',
    };
    
    setEditingBast(newBastData);
  };
  
  const handleEdit = (bastToEdit) => {
    const formattedBast = {
      ...bastToEdit,
      tanggal: bastToEdit.tanggal ? new Date(bastToEdit.tanggal).toISOString().split('T')[0] : '',
    };
    setEditingBast(formattedBast);
  };

  const handleCancelEdit = () => {
    setEditingBast(null);
  };
  
  const handleEntriesChange = (e) => {
    setEntriesPerPage(Number(e.target.value));
    setCurrentPage(1);
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">{t('Bast Management')}</h1>
      <div className="flex justify-between items-center mb-6">
        {/* ✅ Tombol "Create" sekarang memanggil fungsi baru */}
        <button
          onClick={handleCreateClick}
          className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          {t('Create')}
        </button>
        {/* ... Search Input ... */}
      </div>

      <BastList
        bast={bastList}
        searchTerm={searchTerm}
        currentPage={currentPage}
        entriesPerPage={entriesPerPage}
        onPageChange={setCurrentPage}
        onEntriesChange={handleEntriesChange}
        onEdit={handleEdit}
        onDelete={handleDelete}
      />

      {editingBast && (
        <BastForm
          initialData={editingBast}
          onSubmit={(data) => {
            if (editingBast.id) {
              handleUpdate(editingBast.id, data);
            } else {
              handleCreate(data);
            }
          }}
          onCancel={handleCancelEdit}
          isEditing={!!editingBast.id}
          profil={profil}
        />
      )}
    </div>
  );
}

export default Bast;
