import React, { useState, useEffect, useMemo, useCallback } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import { numberToWords } from '../utils/numberToWords';

function SuratForm({ initialData, onSubmit, onCancel, isEditing, profil = [], kategori = [], bankList = [] }) {
  // === STATE MANAGEMENT ===
  const [formData, setFormData] = useState(initialData);
  const [dynamicFields, setDynamicFields] = useState(initialData.meta_data?.dynamicFields || {});
  const [items, setItems] = useState(initialData.meta_data?.items || []);
  const [originalTemplate, setOriginalTemplate] = useState('');
  
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentItem, setCurrentItem] = useState({ deskripsi: '', volume: 1, harga_satuan: '' });

  // === FUNGSI & LOGIKA ===

  // Fungsi untuk meregenerasi konten surat (dipanggil oleh useEffect)
  const regeneratePreview = useCallback(() => {
    if (!originalTemplate) return;

    let newContent = originalTemplate;
    const selectedProfil = profil.find(p => p.id === Number(formData.profil_id));
    const nomorSurat = formData.no_surat || '.../.../.../.../...';
    const tanggalFormatted = formData.tanggal ? new Date(formData.tanggal).toLocaleDateString('id-ID', { day: 'numeric', month: 'long', year: 'numeric' }) : '[Tanggal Surat]';

    newContent = newContent.replace(/{{tanggal_surat}}/g, tanggalFormatted)
      .replace(/{{nama_penerima}}/g, selectedProfil?.nama || '[Nama Penerima]')
      .replace(/{{jabatan_penerima}}/g, selectedProfil?.jabatan || '[Jabatan Penerima]')
      .replace(/{{perusahaan_penerima}}/g, selectedProfil?.instansi || '[Nama Perusahaan Penerima]')
      .replace(/{{nomor_surat}}/g, nomorSurat)
      .replace(/{{nama_penandatangan}}/g, dynamicFields.nama_penandatangan || '[Nama Anda]')
      .replace(/{{jabatan_penandatangan}}/g, dynamicFields.jabatan_penandatangan || '[Jabatan Anda]');
    
    if (formData.kategori_kode === 'SP') {
      const subTotal = items.reduce((sum, item) => sum + (Number(item.volume) || 0) * (Number(item.harga_satuan) || 0), 0);
      const ppn = Math.max(0, Number(dynamicFields.ppn_persen) || 0);
      const pph = Math.max(0, Number(dynamicFields.pph_persen) || 0);
      const ppnAmount = subTotal * (ppn / 100);
      const pphAmount = subTotal * (pph / 100);
      const grandTotal = subTotal + ppnAmount - pphAmount;
      
      const tableRows = items.map((item, index) => `...`).join(''); // Logika tabel sama
      const tableHTML = `...`; // Logika tabel sama
      
      newContent = newContent.replace(/{{jenis_pekerjaan}}/g, dynamicFields.jenis_pekerjaan || '[Jenis Pekerjaan/Produk]')
        .replace('{{tabel_penawaran}}', tableHTML);

    } else if (formData.kategori_kode === 'PP') {
      const jumlah = Number(dynamicFields.jumlah_tagihan) || 0;
      newContent = newContent
        .replace(/{{nama_pekerjaan}}/g, dynamicFields.nama_pekerjaan || '[Nama Pekerjaan]')
        .replace(/{{nomor_spk}}/g, dynamicFields.nomor_spk || '[Nomor SPK]')
        .replace(/{{tanggal_spk}}/g, dynamicFields.tanggal_spk ? new Date(dynamicFields.tanggal_spk).toLocaleDateString('id-ID') : '[Tanggal SPK]')
        .replace(/{{nomor_bast}}/g, dynamicFields.nomor_bast || '[Nomor BAST]')
        .replace(/{{tanggal_bast}}/g, dynamicFields.tanggal_bast ? new Date(dynamicFields.tanggal_bast).toLocaleDateString('id-ID') : '[Tanggal BAST]')
        .replace(/{{jumlah_tagihan}}/g, jumlah.toLocaleString('id-ID'))
        .replace(/{{terbilang_tagihan}}/g, jumlah > 0 ? numberToWords(jumlah) : 'Nol Rupiah')
        .replace(/{{nama_bank}}/g, dynamicFields.nama_bank || '[Nama Bank]')
        .replace(/{{atas_nama_rekening}}/g, dynamicFields.atas_nama_rekening || '[Atas Nama Rekening]')
        .replace(/{{nomor_rekening}}/g, dynamicFields.nomor_rekening || '[Nomor Rekening]');
    }
    
    setFormData(prev => ({ ...prev, isi: newContent }));
  }, [originalTemplate, formData.profil_id, formData.tanggal, formData.no_surat, dynamicFields, items, profil, formData.kategori_kode]);


  // === EFEK ===

  // Efek 1: Inisialisasi/Reset form saat data dari induk berubah
  useEffect(() => {
    setFormData(initialData);
    setDynamicFields(initialData.meta_data?.dynamicFields || {});
    setItems(initialData.meta_data?.items || []);

    const selectedKategori = kategori.find(k => k.kode === initialData.kategori_kode);
    setOriginalTemplate(selectedKategori?.template || '');
  }, [initialData, kategori]);
  
  // Efek 2: Panggil fungsi regenerasi preview setiap ada perubahan
  useEffect(() => {
    regeneratePreview();
  }, [regeneratePreview]);


  // === HANDLER ===

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });
  const handleDynamicChange = (e) => {
    const { name, value } = e.target;
    // ✅ KOREKSI: Tangani input koma untuk PPN/PPH
    if (name === 'ppn_persen' || name === 'pph_persen') {
      setDynamicFields(prev => ({ ...prev, [name]: value.replace(',', '.') }));
    } else {
      setDynamicFields(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleItemFormChange = (e) => {
  const { name, value } = e.target;
  setCurrentItem(prev => ({ ...prev, [name]: value }));
  };

  const handleSaveItem = () => {
    setItems([...items, currentItem]);
    setCurrentItem({ deskripsi: '', volume: 1, harga_satuan: '' });
    setIsModalOpen(false);
  };
  
  const handleRemoveItem = (indexToRemove) => {
    setItems(items.filter((_, index) => index !== indexToRemove));
  };
  
const generateOfferTable = () => {
    // 1. Kalkulasi
    const subTotal = items.reduce((sum, item) => sum + (Number(item.volume) || 0) * (Number(item.harga_satuan) || 0), 0);
    const ppn = Math.max(0, Number(dynamicFields.ppn_persen) || 0);
    const pph = Math.max(0, Number(dynamicFields.pph_persen) || 0);
    const ppnAmount = subTotal * (ppn / 100);
    const pphAmount = subTotal * (pph / 100);
    const grandTotal = subTotal + ppnAmount - pphAmount;

    // 2. Membuat baris-baris tabel dari state 'items'
    const tableRows = items.map((item, index) => `
      <tr>
        <td style="border: 1px solid #dddddd; text-align: center; padding: 8px;">${index + 1}</td>
        <td style="border: 1px solid #dddddd; padding: 8px;">${item.deskripsi || ''}</td>
        <td style="border: 1px solid #dddddd; text-align: center; padding: 8px;">${Number(item.volume) || 0}</td>
        <td style="border: 1px solid #dddddd; text-align: right; padding: 8px;">${(Number(item.harga_satuan) || 0).toLocaleString('id-ID')}</td>
        <td style="border: 1px solid #dddddd; text-align: right; padding: 8px;">${((Number(item.volume) || 0) * (Number(item.harga_satuan) || 0)).toLocaleString('id-ID')}</td>
      </tr>
    `).join('');

    // 3. Menggabungkan semua menjadi satu string HTML tabel
    const tableHTML = `
      <table style="width: 100%; border-collapse: collapse;">
        <thead>
          <tr style="background-color: #f2f2f2;">
            <th style="border: 1px solid #dddddd; text-align: center; padding: 8px;">No</th>
            <th style="border: 1px solid #dddddd; text-align: left; padding: 8px;">Deskripsi</th>
            <th style="border: 1px solid #dddddd; text-align: center; padding: 8px;">Volume</th>
            <th style="border: 1px solid #dddddd; text-align: right; padding: 8px;">Harga Satuan</th>
            <th style="border: 1px solid #dddddd; text-align: right; padding: 8px;">Total Harga</th>
          </tr>
        </thead>
        <tbody>
          ${items.length > 0 ? tableRows : `<tr><td colspan="5" style="text-align: center; padding: 8px;"><i>Belum ada item ditambahkan.</i></td></tr>`}
          <tr>
            <td colspan="4" style="border: 1px solid #dddddd; text-align: right; padding: 8px;"><strong>Sub Total</strong></td>
            <td style="border: 1px solid #dddddd; text-align: right; padding: 8px;"><strong>${subTotal.toLocaleString('id-ID')}</strong></td>
          </tr>
          <tr>
            <td colspan="4" style="border: 1px solid #dddddd; text-align: right; padding: 8px;"><strong>PPN ${ppn}%</strong></td>
            <td style="border: 1px solid #dddddd; text-align: right; padding: 8px;"><strong>${ppnAmount.toLocaleString('id-ID')}</strong></td>
          </tr>
          <tr>
            <td colspan="4" style="border: 1px solid #dddddd; text-align: right; padding: 8px;"><strong>PPh ${pph}%</strong></td>
            <td style="border: 1px solid #dddddd; text-align: right; padding: 8px;"><strong>(${pphAmount.toLocaleString('id-ID')})</strong></td>
          </tr>
          <tr>
            <td colspan="4" style="border: 1px solid #dddddd; text-align: right; padding: 8px; background-color: #f2f2f2;"><strong>Grand Total</strong></td>
            <td style="border: 1px solid #dddddd; text-align: right; padding: 8px; background-color: #f2f2f2;"><strong>${grandTotal.toLocaleString('id-ID')}</strong></td>
          </tr>
        </tbody>
      </table>`;
    
    // 4. Return hasilnya agar bisa digunakan
    return { tableHTML, grandTotal };
  };

  // ✅ FIX 4: Mengirim `meta_data` saat submit
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.profil_id || !formData.kategori_kode || !formData.tanggal) {
      alert('Tujuan, Kategori, dan Tanggal wajib diisi.');
      return;
    }
    const dataToSend = {
      ...formData,
      meta_data: {
        dynamicFields,
        items,
      },
    };
    onSubmit(dataToSend);
  };
  
  const modules = useMemo(() => ({
    toolbar: [
      [{ 'header': [1, 2, 3, false] }],
      ['bold', 'italic', 'underline', 'strike'],
      [{ 'list': 'ordered'}, { 'list': 'bullet' }],
      ['link', 'image', 'table'],
    ],
  }), []);

  // === RENDER JSX ===
  return (
    <>
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
        <div className="bg-white rounded-lg shadow-lg w-full max-w-4xl flex flex-col max-h-[95vh]">
          <h3 className="text-xl font-bold p-6 pb-4 flex-shrink-0">
            {isEditing ? 'Edit Surat' : 'Tambah Surat'}
          </h3>
          
          <div className="flex-grow overflow-y-auto px-6 pb-6">
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="tanggal" className="block text-sm font-medium">Tanggal Surat</label>
                  <input id="tanggal" name="tanggal" type="date" value={formData.tanggal || ''} onChange={handleChange} required className="mt-1 w-full border p-2 rounded-md" />
                </div>
                <div>
                  <label htmlFor="profil_id" className="block text-sm font-medium">Tujuan Surat (Profil)</label>
                  <select id="profil_id" name="profil_id" value={formData.profil_id || ''} onChange={handleChange} required className="mt-1 w-full border p-2 rounded-md">
                    <option value="">-- Pilih Profil Tujuan --</option>
                    {profil.map(p => (<option key={p.id} value={p.id}>{p.nama}</option>))}
                  </select>
                </div>
                <div className="md:col-span-2">
                  <label htmlFor="kategori_kode" className="block text-sm font-medium">Kategori</label>
                  <select id="kategori_kode" name="kategori_kode" value={formData.kategori_kode || ''} onChange={handleChange} required disabled={isEditing} className="mt-1 w-full border p-2 rounded-md">
                    <option value="">-- Pilih Kategori Surat --</option>
                    {kategori.map(k => (<option key={k.kode} value={k.kode}>{k.nama_kategori} ({k.kode})</option>))}
                  </select>
                </div>
              </div>

              {/* --- BAGIAN FORM DINAMIS (BERDASARKAN KATEGORI) --- */}
              {formData.kategori_kode && (
                <div className="p-4 border-l-4 border-gray-300 bg-gray-50 my-4 rounded-r-lg space-y-4">
                  <h4 className="font-bold text-gray-800 mb-2">Detail Tambahan</h4>
                  
                  {/* Field umum, TANPA nomor surat */}
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <input name="nama_penandatangan" value={dynamicFields.nama_penandatangan || ''} onChange={handleDynamicChange} placeholder="Nama Penandatangan" className="border p-2 rounded" />
                    <input name="jabatan_penandatangan" value={dynamicFields.jabatan_penandatangan || ''} onChange={handleDynamicChange} placeholder="Jabatan Penandatangan" className="border p-2 rounded" />
                  </div>

                  {/* === FORM SPESIFIK SURAT PENAWARAN (SP) === */}
                  {formData.kategori_kode === 'SP' && (<>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4 border-t pt-4">
                      <input name="jenis_pekerjaan" value={dynamicFields.jenis_pekerjaan || ''} onChange={handleDynamicChange} placeholder="Jenis Pekerjaan/Produk" className="border p-2 rounded md:col-span-3" />
                      <div className="flex items-center gap-2">
                        <input name="ppn_persen" type="number" step="any" min="0" value={dynamicFields.ppn_persen || ''} onChange={handleDynamicChange} className="border p-2 rounded w-full" placeholder="PPN"/>
                        <span>%</span>
                      </div>
                      <div className="flex items-center gap-2">
                        <input name="pph_persen" type="number" step="any" min="0" value={dynamicFields.pph_persen || ''} onChange={handleDynamicChange} className="border p-2 rounded w-full" placeholder="PPh"/>
                        <span>%</span>
                      </div>
                    </div>
                      <div>
                      <button type="button" onClick={() => setIsModalOpen(true)} className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
                        + Tambah Item Penawaran
                      </button>
                    </div>
                    <div className="space-y-2">
                      {items.map((item, index) => (
                        <div key={index} className="flex items-center gap-2 p-2 bg-blue-100 rounded">
                          <span className="font-semibold">{index + 1}.</span>
                          <span className="flex-grow">{item.deskripsi}</span>
                          <span>(Vol: {item.volume}, Harga: {Number(item.harga_satuan).toLocaleString('id-ID')})</span>
                          <button type="button" onClick={() => handleRemoveItem(index)} className="text-red-500 font-bold">X</button>
                        </div>
                      ))}
                    </div>
                  </>)}
                  
                  {/* === FORM SPESIFIK PERMOHONAN PEMBAYARAN (PP) === */}
                  {formData.kategori_kode === 'PP' && (<>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4 border-t pt-4">
                      <input name="nama_pekerjaan" value={dynamicFields.nama_pekerjaan || ''} onChange={handleDynamicChange} placeholder="Nama Pekerjaan" className="border p-2 rounded-md" />
                      <input name="jumlah_tagihan" type="number" value={dynamicFields.jumlah_tagihan || ''} onChange={handleDynamicChange} placeholder="Jumlah Tagihan (Rp)" className="border p-2 rounded-md" />
                      <input name="nomor_spk" value={dynamicFields.nomor_spk || ''} onChange={handleDynamicChange} placeholder="Nomor SPK" className="border p-2 rounded-md" />
                      <input name="tanggal_spk" type="date" value={dynamicFields.tanggal_spk || ''} onChange={handleDynamicChange} className="border p-2 rounded-md" />
                      <input name="nomor_bast" value={dynamicFields.nomor_bast || ''} onChange={handleDynamicChange} placeholder="Nomor BAST" className="border p-2 rounded-md" />
                      <input name="tanggal_bast" type="date" value={dynamicFields.tanggal_bast || ''} onChange={handleDynamicChange} className="border p-2 rounded-md" />
                    </div>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4 border-t pt-4">
                      <select name="nama_bank" value={dynamicFields.nama_bank || ''} onChange={handleDynamicChange} className="border p-2 rounded-md">
                        <option value="">-- Pilih Bank --</option>
                        {bankList.map(bank => (
                          <option key={bank.id} value={bank.nama_bank}>{bank.nama_bank}</option>
                        ))}
                      </select>
                      <input name="nomor_rekening" value={dynamicFields.nomor_rekening || ''} onChange={handleDynamicChange} placeholder="Nomor Rekening" className="border p-2 rounded-md" />
                      <input name="atas_nama_rekening" value={dynamicFields.atas_nama_rekening || ''} onChange={handleDynamicChange} placeholder="Atas Nama Rekening" className="border p-2 rounded-md" />
                    </div>
                  </>)}
                </div>
              )}
              
              {/* --- EDITOR QUILL --- */}
              <div>
                <label className="block text-sm font-medium">Preview Isi Surat</label>
                <ReactQuill
                  theme="snow"
                  value={formData.isi || ''}
                  onChange={(content) => setFormData(prev => ({ ...prev, isi: content }))}
                  modules={modules}
                  className="mt-1"
                />
              </div>

              {/* --- TOMBOL AKSI --- */}
              <div className="flex justify-end pt-4 gap-2 border-t mt-4">
                <button type="button" onClick={onCancel} className="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700">Batal</button>
                <button type="submit" className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700">{isEditing ? 'Simpan Perubahan' : 'Buat Surat'}</button>
              </div>
            </form>
          </div>
        </div>
      </div>

      {/* ===== MODAL UNTUK MENAMBAH ITEM PENAWARAN ===== */}
      {isModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-[60]">
          <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-lg space-y-4">
            <h3 className="text-xl font-bold">Tambah Item Penawaran</h3>
            <input name="deskripsi" placeholder="Deskripsi Item/Pekerjaan" className="w-full border p-2 rounded" onChange={handleItemFormChange} />
            <div className="grid grid-cols-2 gap-4">
              <input name="volume" type="number" min="1" placeholder="Volume/Qty" className="w-full border p-2 rounded" onChange={handleItemFormChange} />
              <input name="harga_satuan" type="number" min="0" placeholder="Harga Satuan (Rp)" className="w-full border p-2 rounded" onChange={handleItemFormChange} />
            </div>
            <div className="flex justify-end gap-2">
              <button type="button" onClick={() => setIsModalOpen(false)} className="px-4 py-2 bg-gray-600 text-white rounded">Batal</button>
              <button type="button" onClick={handleSaveItem} className="px-4 py-2 bg-blue-600 text-white rounded">Simpan Item</button>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default SuratForm;
