// src/pages/SuratKeluarDetailPages.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getSuratKeluarById } from '../api';

// Style untuk kertas A4 di layar
const pageStyle = {
    background: 'white',
    width: '210mm',
    minHeight: '297mm',
    marginLeft: 'auto',
    marginRight: 'auto',
    padding: '20mm',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)',
};

// ✅ KOREKSI: Tambahkan style khusus untuk print
const printStyles = `
  @media print {
    /* Sembunyikan semua elemen di body secara default */
    body * {
      visibility: hidden;
    }
    
    /* Tombol dan elemen lain yang tidak perlu dicetak akan hilang */
    .no-print, .no-print * {
      display: none !important;
    }

    /* Tampilkan hanya area surat dan semua isinya */
    .printable-area, .printable-area * {
      visibility: visible;
    }

    /* Posisikan area surat agar pas di halaman cetak */
    .printable-area {
      position: absolute;
      left: 0;
      top: 0;
      width: 100%;
      padding: 0;
      margin: 0;
      box-shadow: none;
    }
  }
`;

function SuratKeluarDetailPages() {
    const [letter, setLetter] = useState(null);
    const [loading, setLoading] = useState(true);
    const { id } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        const fetchLetter = async () => {
            try {
                const res = await getSuratKeluarById(id);
                setLetter(res.data.data);
            } catch (error) {
                alert('Gagal memuat detail surat.');
            } finally {
                setLoading(false);
            }
        };
        fetchLetter();
    }, [id]);

    const handlePrint = () => window.print();

    if (loading) return <div className="p-6 text-center">Memuat dokumen...</div>;
    if (!letter) return <div className="p-6 text-center">Dokumen tidak ditemukan.</div>;

    return (
        <div className="bg-gray-100 p-8">
            {/* Tambahkan tag style di sini */}
            <style>{printStyles}</style>

            <div className="action-buttons no-print max-w-4xl mx-auto mb-4 flex justify-between">
                <button onClick={() => navigate('/surat-keluar')} className="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700">Kembali</button>
                <button onClick={handlePrint} className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">Cetak Surat</button>
            </div>
            
            {/* ✅ KOREKSI: Tambahkan className 'printable-area' */}
            <div style={pageStyle} className="document-paper printable-area">
                <div dangerouslySetInnerHTML={{ __html: letter.isi_surat_final }} />
            </div>
        </div>
    );
}
export default SuratKeluarDetailPages;
