import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { Navigate } from 'react-router-dom';
import { getKategori, createKategori, updateKategori, deleteKategori } from '../api';
import KategoriList from '../components/KategoriList';
import KategoriForm from '../components/KategoriForm';
import { useTranslation } from '../hooks/useTranslation';

function Kategori() {
  const { isAuthenticated, loading } = useAuth();
  const { t } = useTranslation();
  const [kategori, setKategori] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [entriesPerPage, setEntriesPerPage] = useState(10);
  const [editingKategori, setEditingKategori] = useState(null);

  if (loading) return <div>Loading...</div>;
  if (!isAuthenticated()) return <Navigate to="/login" />;

    useEffect(() => {
    const fetchKategori = async () => {
      try {
        const res = await getKategori();
        const data = res.data?.data;

        if (Array.isArray(data)) {
          setKategori(data);
        } else {
          console.warn('⚠️ Data kategori bukan array:', data);
          setKategori([]);
        }
      } catch (err) {
        console.error('Gagal ambil data kategori:', err);
        alert(t('Gagal memuat data kategori. Cek koneksi atau database.'));
      }
    };
    fetchKategori();
  }, []); // Menambahkan t sebagai dependency jika digunakan di dalam alert

  // Fungsi untuk refresh data
  const refreshKategori = async () => {
    try {
        const res = await getKategori();
        setKategori(res.data?.data || []);
    } catch (error) {
        console.error("Gagal me-refresh data kategori:", error);
    }
  }

  // Tambah data
  const handleCreate = async (data) => {
    try {
      await createKategori(data);
      await refreshKategori();
      setEditingKategori(null);
    } catch (err) {
      console.error('Gagal tambah kategori:', err);
      alert(t('Gagal menambahkan data kategori.'));
    }
  };

  // Update data
  const handleUpdate = async (kode, data) => {
    try {
      await updateKategori(kode, data);
      await refreshKategori();
      setEditingKategori(null);
    } catch (err) {
      console.error('Gagal update kategori:', err);
      alert(t('Gagal memperbarui data kategori.'));
    }
  };

  // Hapus data
  const handleDelete = async (kode) => {
    if (window.confirm(t('Apakah Anda yakin ingin menghapus data ini?'))) {
      try {
        await deleteKategori(kode);
        await refreshKategori();
      } catch (err) {
        console.error('Gagal hapus kategori:', err);
        alert(t('Gagal menghapus data kategori.'));
      }
    }
  };

  // ✅ KOREKSI: Pastikan 'template' tidak pernah null saat dikirim ke form
  const handleEdit = (kategoriToEdit) => {
    setEditingKategori({
      ...kategoriToEdit,
      template: kategoriToEdit.template || '',
    });
  };

  // Tutup modal
  const handleCancelEdit = () => {
    setEditingKategori(null);
  };

  // Handle perubahan jumlah entri
  const handleEntriesChange = (e) => {
    const value = Number(e.target.value);
    if (value > 0) {
      setEntriesPerPage(value);
      setCurrentPage(1);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-6">{t('Kategori Management')}</h1>

      <div className="flex justify-between items-center mb-6">
        <button
          onClick={() =>
            setEditingKategori({
              kode: '',
              // ✅ KOREKSI: Konsistensi nama field dengan form
              nama_kategori: '', 
              template: '',
            })
          }
          className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          {t('Create')}
        </button>
        <div className="relative">
          <input
            type="text"
            placeholder={t('Search')}
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-10 pr-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <svg
            className="w-5 h-5 absolute left-3 top-2.5 text-gray-500"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
            ></path>
          </svg>
        </div>
      </div>

      <KategoriList
        kategori={kategori}
        searchTerm={searchTerm}
        currentPage={currentPage}
        entriesPerPage={entriesPerPage}
        onPageChange={setCurrentPage}
        onEntriesChange={handleEntriesChange}
        onEdit={handleEdit}
        onDelete={handleDelete}
      />

      {editingKategori && (
        <KategoriForm
          formData={editingKategori}
          setFormData={setEditingKategori} // <-- Menambahkan prop ini jika KategoriForm membutuhkannya
          onSubmit={(data) => {
            if (editingKategori.kode && isEditing) { // Memastikan isEditing juga dicek
              handleUpdate(editingKategori.kode, data);
            } else {
              handleCreate(data);
            }
          }}
          onCancel={handleCancelEdit}
          isEditing={!!editingKategori.kode}
        />
      )}
    </div>
  );
}

export default Kategori;
